this.focus();

let totalArt = 0;
let t = 0;

let defaultSize = {
  width: 1000,
  height: 1000,
};
let backgroundImage;
let backgroundColor;
let backgroundMode;
let elementImages = [];
let items = [];
let namer;
let modes = [];
let backgrounds = [];
let maxElements = 30;
let minElements = 5;
const backgroundModes = {
  Color: "color",
  Image: "image",
};
const itemModes = {
  Random: "random",
  Center: "center",
};
let itemMode = itemModes.Random;

// this function will load all media before setup()
function preload() {
  for (let i = 0; i <= 13; i++) {
    let fname = `backgrounds/background.${i.toString().padStart(5, "0")}.jpeg`;
    backgrounds[i] = loadImage(fname);
  }

  // load the rest of the images
  // change 5 to match your total number of images
  // alternatively (since this takes a while)
  // do a callback
  for (let i = 0; i <= 33; i++) {
    let fname = `images/${i.toString().padStart(6, "0")}.png`;
    elementImages[i] = loadImage(fname);
  }
}

function setup() {
  modes = [
    "source-over", // if you use the underlying value, it can be outside of setup
    // see https://github.com/processing/p5.js/blob/v1.6.0/src/core/constants.js#LL451C22-L451C35
    BLEND,
    BLEND,
    BLEND,
    DARKEST,
    DARKEST,
    LIGHTEST,
    LIGHTEST,
    DIFFERENCE,
    MULTIPLY,
    MULTIPLY,
    MULTIPLY,
    EXCLUSION,
    SCREEN,
    SCREEN,
    OVERLAY,
    OVERLAY,
    HARD_LIGHT,
    SOFT_LIGHT,
    SOFT_LIGHT,
    SOFT_LIGHT,
    DODGE,
    DODGE,
    DODGE,
    BURN,
    BURN,
    ADD,
    ADD,
  ];
  // TODO: first time through, we need a pre-determined size
  reset();
  makeCollage();
}

const pickBackground = () => {
  backgroundMode = random(Object.values(backgroundModes));
  switch (backgroundMode) {
    case backgroundModes.Image:
      backgroundImage = random(backgrounds);
      break;

    case backgroundModes.Color:
    default:
      backgroundColor = color(random(255), random(255), random(255));
      break;
  }
};

const renderBackground = () => {
  switch (backgroundMode) {
    case backgroundModes.Image:
      image(
        backgroundImage,
        0,
        0,
        backgroundImage.width,
        backgroundImage.height
      );
      break;

    case backgroundModes.Color:
    default:
      background(backgroundColor);
      break;
  }
};

const reset = () => {
  namer = filenamer(datestring());
  pickBackground();
  if (backgroundImage) {
    createCanvas(backgroundImage.width, backgroundImage.height);
  } else {
    createCanvas(defaultSize.width, defaultSize.height);
  }
};

function makeCollage() {
  blendMode(BLEND);
  clear();
  renderBackground();
  let totalItems = random(minElements, maxElements);

  // item picks the image
  // but what if WE picked the image
  // and then a random number of them
  // BUT the blend mode was the same for all of the same image?
  items = [];
  for (let i = 0; i < totalItems; i++) {
    items.push(
      itemMode === itemModes.Center ? new ItemCentered() : new ItemRandomImage()
    );
  }

  items.forEach((item) => item.display());
}

function keyPressed() {
  // console.log(keyCode);
  if (keyCode === UP_ARROW) {
    maxElements++
  }
  else if (keyCode === DOWN_ARROW) {
    maxElements = Math.max(maxElements - 1, minElements)
  }
}

function keyTyped() {
  if (key === "d") {
    makeCollage();
  } else if (key === "s") {
    saveCanvas(namer(), "png");
  } else if (key === "r") {
    reset();
    makeCollage();
  } else if (key === "i") {
    const modeIndex = Object.values(itemModes).indexOf(itemMode);
    const nextIndex = (modeIndex + 1) % Object.keys(itemModes).length;
    itemMode = Object.values(itemModes)[nextIndex];
    makeCollage();
  }
  return false
}

const resize = (imgWidth, imgHeight, maxWidth) => ({
  w: maxWidth,
  h: (maxWidth / imgWidth) * imgHeight,
});

class Item {
  constructor(image) {
    this.flower = image;
    this.size = resize(this.flower.width, this.flower.height, random(40, 512));
    this.x = random(-(this.size.w / 2), width - 40);
    this.y = random(-(this.size.h / 2), height - 40);
    this.mode = random(modes);
  }

  display() {
    blendMode(this.mode);
    image(this.flower, this.x, this.y, this.size.w, this.size.h);
  }
}

class ItemRandomImage extends Item {
  // It's all random :person-shrugging:
  constructor() {
    super(random(elementImages));
  }
}

class ItemCentered extends ItemRandomImage {
  // everything is centered
  constructor() {
    super();
    this.x = width / 2 - this.size.w / 2;
    this.y = height / 2 - this.size.h / 2;
  }
}

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

const datestring = () => {
  const d = new Date()
  const year = d.getFullYear()
  const month = String(d.getMonth() + 1).padStart(2, '0')
  const day = String(d.getDate()).padStart(2, '0')
  const hour = String(d.getHours()).padStart(2, '0')
  const min = String(d.getMinutes()).padStart(2, '0')
  const secs = String(d.getSeconds()).padStart(2, '0')
  return `${year}${month}${day}${hour}${min}${secs}`
}

const filenamer = (prefix) => {
  let frame = 0
  return () => {
    const name = `collage.${prefix}-${String(frame).padStart(6, '0')}`
    frame += 1
    return name
  }
}

/* global p5 */

const sketch = p => {
  let img
  let cellCount = 10
  let circleColor
  let slider
  let colorPicker
  let transparencySlider
  let offscreen
  let dirty = true
  const modal = {
    showHelp: false,
    showUI: true
  }

  function generateFilename (prefix) {
    const d = new Date()
    return (
      prefix + '.' +
      d.getFullYear() +
      '.' +
      (d.getMonth() + 1) +
      '.' +
      d.getDate() +
      d.getHours() +
      d.getMinutes() +
      d.getSeconds() +
      '.png'
    )
  }

  p.preload = () => {
    img = p.loadImage('images/mona.png')
  }

  p.setup = () => {
    const canvasWidth = p.windowWidth - 20
    const canvasHeight = p.windowHeight - 20
    const scaleFactor = Math.min(
      canvasWidth / img.width,
      canvasHeight / img.height
    )
    const imgWidth = img.width * scaleFactor
    const imgHeight = img.height * scaleFactor

    p.createCanvas(imgWidth, imgHeight)
    circleColor = p.color(0, 255, 238)

    slider = p.createSlider(1, 25, cellCount)
    slider.position(10, imgHeight - 60)
    slider.style('width', '200px')
    slider.attribute('title', 'Cell Size')
    slider.input(() => {
      cellCount = 26 - slider.value()
      renderOffscreen()
    })

    colorPicker = p.createColorPicker(circleColor)
    colorPicker.position(220, imgHeight - 30)
    colorPicker.input(() => {
      updateCircleColor()
      renderOffscreen()
    })

    transparencySlider = p.createSlider(0, 255, 255)
    transparencySlider.position(10, imgHeight - 30)
    transparencySlider.style('width', '200px')
    transparencySlider.attribute('title', 'Transparency')
    transparencySlider.input(() => {
      updateCircleColor()
      renderOffscreen()
    })

    offscreen = p.createGraphics(img.width, img.height)
    renderOffscreen()
  }

  p.draw = () => {
    const scaleFactor = Math.min(p.width / img.width, p.height / img.height)
    const imgWidth = img.width * scaleFactor
    const imgHeight = img.height * scaleFactor

    if (dirty) {
      p.background(255)
      p.image(offscreen, 0, 0, imgWidth, imgHeight)
      dirty = false
    }

    if (modal.showUI) {
      drawUI()
    }

    if (modal.showHelp) {
      displayHelpScreen()
    }
  }

  function renderOffscreen () {
    offscreen.image(img, 0, 0, img.width, img.height)
    const cellSize = img.width / cellCount
    const cols = cellCount
    const rows = Math.floor(img.height / cellSize)
    const adjustedCellHeight = img.height / rows

    for (let y = 0; y < rows; y++) {
      for (let x = 0; x < cols; x++) {
        const diameter = p.random(
          Math.min(cellSize, adjustedCellHeight) * 0.5,
          Math.min(cellSize, adjustedCellHeight)
        )
        offscreen.fill(circleColor)
        offscreen.ellipse(
          x * cellSize + cellSize / 2,
          y * adjustedCellHeight + adjustedCellHeight / 2,
          diameter,
          diameter
        )
      }
    }
    dirty = true
  }

  function updateCircleColor () {
    const color = p.color(colorPicker.value())
    const alpha = transparencySlider.value()
    circleColor = p.color(color.levels[0], color.levels[1], color.levels[2], alpha)
  }

  function drawUI () {
    const uiText = [
      `Cells count: ${cellCount}`,
      `Image Size: ${img.width} x ${img.height}`
    ]

    const boxWidth = 230
    const boxHeight = uiText.length * 20 + 20

    p.fill(0, 150)
    p.noStroke()
    p.rect(5, p.height - boxHeight - 65, boxWidth, boxHeight, 10)

    p.fill('white')
    p.textSize(16)
    p.textAlign(p.LEFT, p.TOP)
    uiText.forEach((text, index) => {
      p.text(text, 10, p.height - boxHeight - 55 + index * 20)
    })
  }

  function displayHelpScreen () {
    p.fill(50, 150)
    p.rect(50, 50, p.width - 100, p.height - 100, 10)

    p.fill(255)
    p.textSize(16)
    p.textAlign(p.LEFT, p.TOP)
    p.text(
      `
      Help Screen:

      ? - Show/Hide this help screen
      r - Re-render image
      s - Save image
      u - Show/Hide UI
      `,
      70,
      70
    )
  }

  p.keyPressed = () => {
    if (p.key === 'h') {
      modal.showUI = !modal.showUI
      dirty = true
    } else if (p.key === 's') {
      saveImage()
    } else if (p.key === 'r') {
      renderOffscreen()
      dirty = true
    } else if (p.key === '?') {
      modal.showHelp = !modal.showHelp
      dirty = true
    } else if (p.key === 'u') {
      modal.showUI = !modal.showUI
      dirty = true
    }
  }

  function saveImage () {
    p.save(offscreen, generateFilename('circle_grid'))
  }
}

new p5(sketch)

