precision mediump float;
uniform vec3 iResolution;           // viewport resolution (in pixels)
uniform sampler2D u_image;          // input channel. XX = 2D/Cube
varying vec2 v_texCoord;

const float PI = acos(0.0) * 2.0;

void rotate(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

#define SCALE 140.0 // (pow(cos(iTime * 0.15), 4.0) * 88.0 + 8.0)

float dots(vec2 uv, float angle, sampler2D sampler, vec3 col) {
    rotate(uv, angle);

    vec2 samplePos = floor(uv + 0.5);
    rotate(samplePos, -angle);
    samplePos = samplePos / 96.0 * vec2(iResolution.y / iResolution.x, 1) + 0.5;

    vec3 textureCol = texture2D(sampler, samplePos + 0.2).rgb;

    float value;

    // key dots (black)
    if (col == vec3(0)) {
        value = (max(0.5 - textureCol.x, 0.0) + max(0.5 - textureCol.y, 0.0) + max(0.5 - textureCol.z, 0.0)) / 3.0;

    // normal cmy dots
    } else {
        value = dot(1.0 - textureCol, col);

        // area of circle --> radius
        value = sqrt(value / PI);
    }

    // clamp dot size (key fixes this)
    value = min(value, 0.5);

    return float(value > length(fract(uv - 0.5) - 0.5));
}

vec3 render(vec2 fragCoord) {
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);

    uv *= SCALE;

    vec3 col = 1.0 - vec3(
        dots(uv, -15.0 * PI / 180.0, u_image, vec3(1,0,0)) * 0.8,
        dots(uv, -75.0 * PI / 180.0, u_image, vec3(0,1,0)) * 0.8,
        dots(uv, 0.0 * PI / 180.0, u_image, vec3(0,0,1)) * 0.8
    );

    // key
    col -= float(dots(uv, -45.0 * PI / 180.0, u_image, vec3(0))) * 0.8;

    return col;
}

const float ANTI_ALIAS = 4.0;

void main() {

    vec3 col = vec3(0.0);

    vec2 uv = gl_FragCoord.xy / iResolution.xy;
    // vec2 uv = gl_FragCoord.xy;
    // uv.y = 1.0 - uv.y;
    // gl_FragCoord.y = 1.0 - gl_FragCoord.y

    // anti aliasing
    for (float x = -0.5; x < 0.5; x += 1.0 / ANTI_ALIAS) {
        for (float y = -0.5; y < 0.5; y += 1.0 / ANTI_ALIAS) {
            col += render(gl_FragCoord.xy + vec2(x, y)) / (ANTI_ALIAS * ANTI_ALIAS);
        }
    }

    gl_FragColor = vec4(col, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 vTexCoord;

void main() {
  // copy the texcoords
  vTexCoord = aTexCoord;
  vTexCoord.y = 1.0 - vTexCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

/* global p5 */

// https://editor.p5js.org/MichaelPaulukonis/sketches/sGnW8FUdE

// https://www.1001fonts.com/asterisp-font.html

class TextBox {
  constructor(p, x, y, text, font, fillColor = 255, textSize = 102) {
    this.p = p;
    this.x = x;
    this.y = y;
    this.text = text;
    this.font = font;
    this.fillColor = fillColor;
    this.textSize = textSize;
    this.p.textFont(this.font);
    this.p.textSize(this.textSize);
    this.w = this.p.textWidth(this.text);
    this.h = this.p.textAscent();
    this.dragging = false;
    this.offsetX = 0;
    this.offsetY = 0;
    this.strokeColor = this.p.color(
      this.p.random(255),
      this.p.random(255),
      this.p.random(255)
    );
    this.slider = this.p.createSlider(10, 500, this.textSize);
    this.slider.position(this.x, this.y + this.h + 10);
    this.slider.input(() => this.updateTextSize(this.slider.value()));
  }

  updateTextSize(newSize) {
    this.textSize = newSize;
    this.p.textSize(this.textSize);
    this.w = this.p.textWidth(this.text);
    this.h = this.p.textAscent();
    this.slider.position(this.x, this.y + this.h + 10);
  }

  render() {
    this.p.fill(this.fillColor);
    this.p.noStroke();
    this.p.textFont(this.font);
    this.p.textSize(this.textSize);
    this.p.textAlign(this.p.LEFT, this.p.TOP);
    this.p.text(this.text, this.x, this.y);

    if (this.dragging) {
      this.p.noFill();
      this.p.stroke(this.strokeColor);
      this.p.rect(this.x, this.y, this.w, this.h);
      this.p.noStroke();
    }
  }

  containsMouse(x, y) {
    return (
      x > this.x && x < this.x + this.w && y > this.y && y < this.y + this.h
    );
  }
}

const sketch = (p) => {
  let img;
  let font;
  let cellWidth, cellHeight;
  const textBoxes = [];
  const displayWidth = 600;

  const timestamp = () => {
    const d = new Date();
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, "0");
    const day = String(d.getDate()).padStart(2, "0");
    const hour = String(d.getHours()).padStart(2, "0");
    const min = String(d.getMinutes()).padStart(2, "0");
    const secs = String(d.getSeconds()).padStart(2, "0");
    const millis = String(d.getMilliseconds()).padStart(3, "0");
    return `${year}${month}${day}.${hour}${min}${secs}.${millis}`;
  };

  function generateFilename(prefix) {
    return `${prefix || "mona"}-${timestamp()}.png`;
  }

  p.preload = () => {
    img = p.loadImage("assets/mona-lisa-67506.jpg");
    font = p.loadFont("assets/Asterisp Alpha.otf");
  };

  p.setup = () => {
    const c = p.createCanvas(displayWidth, displayWidth);
    c.drop(handleFile);
    calcCell(img)
    p.fill(255);
    p.textSize(102);
    p.textAlign(p.LEFT, p.TOP);
    p.textFont(font);
    const textBoxMona = new TextBox(
      p,
      (p.width - cellWidth) / 2,
      (p.height - cellHeight) / 2,
      "R",
      font,
      255,
      256
    );
    textBoxes.push(textBoxMona);
  };

  p.draw = () => {
    p.background(255);
    const dx = (p.width - cellWidth) / 2;
    const dy = (p.height - cellHeight) / 2;
    p.image(img, dx, dy, cellWidth, cellHeight);

    textBoxes.forEach((textBox) => textBox.render());
  };

  p.mousePressed = () => {
    textBoxes.forEach((textBox) => {
      if (textBox.containsMouse(p.mouseX, p.mouseY)) {
        textBox.dragging = true;
        textBox.offsetX = p.mouseX - textBox.x;
        textBox.offsetY = p.mouseY - textBox.y;
      }
    });
  };

  p.mouseDragged = () => {
    textBoxes.forEach((textBox) => {
      if (textBox.dragging) {
        textBox.x = p.mouseX - textBox.offsetX;
        textBox.y = p.mouseY - textBox.offsetY;
        textBox.slider.position(textBox.x, textBox.y + textBox.h + 10);
      }
    });
  };

  p.mouseReleased = () => {
    textBoxes.forEach((textBox) => {
      textBox.dragging = false;
    });
  };

  p.keyPressed = () => {
    if (p.key === "S") {
      p.saveCanvas(generateFilename("mona-text"));
    }
  };

  function calcCell(img) {
    const aspectRatio = img.width / img.height;
    if (aspectRatio > 1) {
      cellWidth = p.width * 0.7;
      cellHeight = cellWidth / aspectRatio;
    } else {
      cellHeight = p.height * 0.7;
      cellWidth = cellHeight * aspectRatio;
    }
  }

  function handleFile(file) {
    if (file.type === "image") {
      img = p.loadImage(file.data, () => {
        calcCell(img)
        console.log("Image loaded successfully");
      });
    } else {
      console.log("Not an image file!");
    }
  }
};

new p5(sketch); // eslint-disable-line no-new, new-cap

