// Preferred Defaults for p5.js Sketches

// 1. Canvas Setup
// - Default canvas size: 600x600
// - Center the canvas in the window
// - Use a larger off-screen pGraphics object for higher-resolution rendering ("target layer")

// 2. Color and Style
// - Default background color: 255 (white)
// - Default stroke color: 0 (black)
// - Default fill color: 255 (white)

// 3. Interaction
// - Enable mouse and keyboard interaction
// - Include default mousePressed, keyPressed functions
// - Include key handling to show/hide UI
// - Include key handling to save (off-screen target layer)
// - Include a basic help screen describing keys
// - Include a basic UI showing important parameters

// 4. Code Structure
// - Use p5.js instance mode
// - Use standard-js style formatting
// - Organize code into functions and classes as needed
// - Use timestamp and generateFilename functions (below)
// - Place "/* global p5 */" at top of file
// - Instantiate sketch with "new p5(sketch); // eslint-disable-line no-new, new-cap"

// 5. Comments and Documentation
// - Use comments to explain code functionality
// - Include function descriptions and parameter explanations

// 6. Error Handling
// - Include basic error handling for common issues

// DEFAULT CODE

const timestamp = () => {
  const d = new Date()
  const year = d.getFullYear()
  const month = String(d.getMonth() + 1).padStart(2, '0')
  const day = String(d.getDate()).padStart(2, '0')
  const hour = String(d.getHours()).padStart(2, '0')
  const min = String(d.getMinutes()).padStart(2, '0')
  const secs = String(d.getSeconds()).padStart(2, '0')
  const millis = String(d.getMilliseconds()).padStart(3, '0')
  return `${year}${month}${day}.${hour}${min}${secs}.${millis}`
}

function generateFilename (prefix) {
  return `${prefix || 'mona'}-${timestamp()}.png`
}

function displayUI() {
  const uiText = [
    `Shuffle Amount: ${(shuffleProbability * 100).toFixed(0)}%`,
  ];

  const boxWidth = 200;
  const boxHeight = uiText.length * 20 + 20;

  p.fill(0, 150);
  p.noStroke();
  p.rect(5, p.height - boxHeight - 5, boxWidth, boxHeight, 10);

  p.fill('white');
  p.textSize(16);
  p.textAlign(p.LEFT, p.TOP);
  uiText.forEach((text, index) => {
    p.text(text, 10, p.height - boxHeight + 10 + index * 20);
  });
}

function displayHelpScreen() {
  p.fill(50, 150);
  p.rect(50, 50, p.width - 100, p.height - 100, 10);

  p.fill(255);
  p.textSize(16);
  p.textAlign(p.LEFT, p.TOP);
  p.text(
    `
    Help Screen:

    ? - Show/Hide this help screen
    r - Shuffle image
    S - Save image
    h - Show/Hide UI
    `,
    70,
    70
  );
}

/* global p5 chroma themes */

// shaders converted from https://github.com/libretro/glsl-shaders/blob/master/misc/shaders/cmyk-halftone-dot.glsl

// WIP attempting to use 2 shaders

const timestamp = () => {
  const d = new Date();
  const year = d.getFullYear();
  const month = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const hour = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  const secs = String(d.getSeconds()).padStart(2, "0");
  const millis = String(d.getMilliseconds()).padStart(3, "0");
  return `${year}${month}${day}.${hour}${min}${secs}.${millis}`;
};

function generateFilename(prefix) {
  return `${prefix || "mona"}-${timestamp()}.png`;
}

const sketch = (p) => {
  let cmykShader;
  let remapShader;
  let blurShaderH;
  let blurShaderV;
  let cmykEnabled = true;
  let remapEnabled = true;
  let blurEnabled = false;
  let img;
  let cmykLayer;
  let remapLayer;
  let blurLayerH;
  let blurLayerV;
  let targetLayer;
  let frequencySlider;
  let frequency = 20.0;
  let angleSlider;
  let angle = 0.0;
  let dirty = true;
  let autoRotate = false;
  let autoFrequency = false;
  let autoFreqDirection = -1;
  let autoRotateDirection = -1;
  let pause = false;
  let showCrosshairs = false;
  let gridSize = 1;
  let useCMYK = true;
  let autoSave = false;
  let autoSaveCount = 0;
  let pallette;
  let colorTheme;
  let themeIndex = 0;
  let shift = 0;
  let numColors = 8; // Default number of colors
  let density = 1;

  p.preload = () => {
    cmykShader = p.loadShader("2025.01.24.cmyk.vert", "2025.01.24.cmyk.frag");
    remapShader = p.loadShader(
      "2025.01.24.remap.vert",
      "2025.01.24.remap.frag"
    );
    blurShaderH = p.loadShader("2025.01.24.blur.vert", "2025.01.24.blur.frag");
    blurShaderV = p.loadShader("2025.01.24.blur.vert", "2025.01.24.blur.frag");
    // img = p.loadImage('images/multi-color-20250119.194439.824.png')
    img = p.loadImage("images/mona-lisa-6195291.png");
    // img = p.loadImage('images/PXL_20240902_192356810.jpg')
  };

  function setHues(picked) {
    const hues = picked.hues.map((k) => chroma(k).gl().slice(0, 3));

    const arranged = rotateArray(
      [
        chroma(picked.bg).gl().slice(0, 3),
        chroma(picked.fg).gl().slice(0, 3),
        ...hues,
      ],
      shift
    ).slice(0, numColors); // Limit the number of hues

    // Ensure the palette has exactly 8 elements
    while (arranged.length < 8) {
      arranged.push([0.0, 0.0, 0.0]); // Fill with black or any default color
    }

    // 6 hues from theme, plus foreground and background
    pallette = new Float32Array(arranged.flat());
  }

  function rotateArray(arr, shiftAmount) {
    // Handle empty array or no shift needed
    if (!arr.length || shiftAmount === 0) return arr;

    // Normalize shift amount to array length
    const shift = shiftAmount % arr.length;

    // Handle negative shifts
    const normalizedShift = shift >= 0 ? shift : arr.length + shift;

    // Slice and concat to create rotated array
    return arr.slice(normalizedShift).concat(arr.slice(0, normalizedShift));
  }

  const resizeCanvasToImage = () => {
    const defaultMaxSize = 3000;

    // large images throw error:
    // .WebGL-0x1040cfc4700] GL_INVALID_VALUE: Negative offset.
    // fails with image @ size 3468x2611
    // fails with image @ size 3264x2458
    // [.WebGL-0x10417e17100] GL_OUT_OF_MEMORY: Error: 0x00000505, in
    // ../../third_party/angle/src/libANGLE/renderer/gl/RenderbufferGL.cpp,
    // setStorageMultisample:83. Internal error: 0x00000505: Unexpected driver error.

    // The below usually solves for the above issues
    // BUT NOT ALWAYS
    if (img.width > defaultMaxSize || img.height > defaultMaxSize) {
      let newWidth = img.width;
      let newHeight = img.height;

      if (newWidth > newHeight) {
        newHeight = (defaultMaxSize * newHeight) / newWidth;
        newWidth = defaultMaxSize;
      } else {
        newWidth = (defaultMaxSize * newWidth) / newHeight;
        newHeight = defaultMaxSize;
      }

      const tempG = p.createGraphics(newWidth, newHeight);
      tempG.image(img, 0, 0, newWidth, newHeight);
      img = tempG;
    }

    if (!cmykLayer) {
      cmykLayer = p.createGraphics(img.width, img.height, p.WEBGL);
      cmykLayer.shader(cmykShader);
    } else {
      cmykLayer.resizeCanvas(img.width, img.height);
    }

    if (!remapLayer) {
      remapLayer = p.createGraphics(img.width, img.height, p.WEBGL);
      remapLayer.shader(remapShader);
    } else {
      remapLayer.resizeCanvas(img.width, img.height);
    }

    if (!blurLayerH) {
      blurLayerH = p.createGraphics(img.width, img.height, p.WEBGL);
      blurLayerH.shader(blurShaderH);
    } else {
      blurLayerH.resizeCanvas(img.width, img.height);
    }
    if (!blurLayerV) {
      blurLayerV = p.createGraphics(img.width, img.height, p.WEBGL);
      blurLayerV.shader(blurShaderV);
    } else {
      blurLayerV.resizeCanvas(img.width, img.height);
    }

    if (!targetLayer) {
      targetLayer = p.createGraphics(img.width, img.height, p.WEBGL);
    } else {
      targetLayer.resizeCanvas(img.width, img.height);
    }

    // Calculate dimensions maintaining aspect ratio
    let w = img.width;
    let h = img.height;
    const maxDim = 600;

    if (w > maxDim || h > maxDim) {
      if (w > h) {
        h = (maxDim * h) / w;
        w = maxDim;
      } else {
        w = (maxDim * w) / h;
        h = maxDim;
      }
    }
    p.resizeCanvas(w, h);

    // Update shader resolution uniform
    cmykShader.setUniform("iResolution", [img.width, img.height]);
    cmykShader.setUniform("density", density);
    // remap shader doesn't need?
    dirty = true;
  };

  p.setup = () => {
    p.createCanvas(p.windowWidth, p.windowHeight).drop(handleFile);
    density = p.pixelDensity();

    resizeCanvasToImage();

    colorTheme = p.random(themes);
    setHues(colorTheme);

    frequencySlider = p.createSlider(1, 300, frequency);
    frequencySlider.position(10, 10);
    frequencySlider.attribute("title", "frequency");
    frequencySlider.input(() => {
      frequency = frequencySlider.value();
      dirty = true;
    });

    angleSlider = p.createSlider(0, 360, angle);
    angleSlider.position(10, 30);
    angleSlider.attribute("title", "angle");
    angleSlider.input(() => {
      angle = angleSlider.value();
      dirty = true;
    });

    // Create radio button group
    const radioGroup = p.createRadio();
    radioGroup.position(10, 50);
    radioGroup.option("CMYK", "CMYK");
    radioGroup.option("RGB", "RGB");
    radioGroup.selected("CMYK");
    radioGroup.style("width", "200px");
    radioGroup.changed(() => {
      useCMYK = radioGroup.value() === "CMYK";
      dirty = true;
    });
  };

  // more complicated values for autoRotate and autoFrequency

  p.draw = () => {
    if (!pause) {
      if (autoSave) {
        if (angle >= 360) {
          autoSave = false; // Stop when we reach 360
          dirty = true;
          p.frameRate(60);
          return;
        }

        targetLayer.save(
          generateFilename(
            `mona-cmyk_${String(autoSaveCount).padStart(4, "0")}`
          )
        );
        angle += 5; // Increment by 5 degrees
        angleSlider.value(angle);
        autoSaveCount++;
        dirty = true;
      } else if (autoRotate) {
        // atm, I prefer angle t contiously spin
        // if (angle <= 0 || angle >= 360) {
        //   autoRotateDirection *= -1
        // }
        angle = (angle + 1) % 360;
        angleSlider.value(angle);
        dirty = true;
      }
      if (autoFrequency) {
        if (frequency <= 1 || frequency >= 150) {
          autoFreqDirection *= -1;
        }
        frequency = frequency + autoFreqDirection;
        frequencySlider.value(frequency);
        dirty = true;
      }
    }

    if (!dirty) return;
    let currentLayer = img; // Start with the original image

    if (cmykEnabled) {
      cmykLayer.shader(cmykShader);
      cmykShader.setUniform("iChannel0", currentLayer);
      cmykShader.setUniform("frequency", frequency);
      cmykShader.setUniform("angle", angle);
      cmykShader.setUniform("cmyk_flag", useCMYK);
      cmykLayer.rect(0, 0, cmykLayer.width, cmykLayer.height);
      currentLayer = cmykLayer;
    }
    if (remapEnabled) {
      remapLayer.shader(remapShader);
      remapShader.setUniform("u_image", currentLayer);
      remapShader.setUniform("u_palette", pallette);
      remapShader.setUniform("u_numColors", numColors);
      remapLayer.rect(0, 0, remapLayer.width, remapLayer.height);
      currentLayer = remapLayer;
    }
//     if (blurEnabled) {
//       blurShaderH.setUniform("tex0", currentLayer);
//       blurShaderH.setUniform("texelSize", [
//         1.0 / currentLayer.width,
//         1.0 / currentLayer.height,
//       ]);
//       blurShaderH.setUniform("direction", [1.0, 1.0]);
//       blurLayerH.rect(0, 0, blurLayerH.width, blurLayerH.height);

//       blurShaderV.setUniform("tex0", blurLayerH);
//       blurShaderV.setUniform("texelSize", [
//         1.0 / currentLayer.width,
//         1.0 / currentLayer.height,
//       ]);
//       blurShaderV.setUniform("direction", [1.0, 1.0]);
//       blurLayerV.rect(0, 0, blurLayerV.width, blurLayerV.height);

//       currentLayer = blurLayerV;
//     }

    targetLayer.image(
      currentLayer,
      0,
      0,
      targetLayer.width,
      targetLayer.height
    );

    p.clear();
    p.image(targetLayer, 0, 0, p.width, p.height);
    if (showCrosshairs) {
      displayGrid(p, gridSize);
    }
    displayUI();
    dirty = false;
  };

  p.keyPressed = () => {
    if (p.key === "1") {
      cmykEnabled = !cmykEnabled;
      dirty = true;
    } else if (p.key === "!") {
      remapEnabled = !remapEnabled;
      dirty = true;
    } else if (p.key === "z") {
      // TODO: find a better key
      colorTheme = p.random(themes);
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "h") {
      shift -= 1;
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "l") {
      shift += 1;
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "j") {
      themeIndex = (themeIndex - 1 + themes.length) % themes.length;
      colorTheme = themes[themeIndex];
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "k") {
      themeIndex = (themeIndex + 1) % themes.length;
      colorTheme = themes[themeIndex];
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "r") {
      shift = 0;
      setHues(colorTheme);
      dirty = true;
    } else if ("2345678".indexOf(p.key) !== -1) {
      numColors = Number.parseInt(p.key, 10);
      setHues(colorTheme);
      dirty = true;
    } else if (p.key === "c") {
      showCrosshairs = !showCrosshairs;
      dirty = true;
    } else if (showCrosshairs && p.key === "]") {
      gridSize = Math.min(10, gridSize + 1); // Limit to 10 for example
      dirty = true;
    } else if (showCrosshairs && p.key === "[") {
      gridSize = Math.max(1, gridSize - 1);
      dirty = true;
    } else if ((!autoRotate || pause) && (p.key === "r" || p.key === "R")) {
      const direction = p.key === "r" ? 1 : -1;
      angle = (angle + direction) % 360;
      angleSlider.value(angle);
      dirty = true;
    } else if (!(autoFrequency || pause) && (p.key === "f" || p.key === "F")) {
      const direction = p.key === "f" ? 1 : -1;
      frequency = ((frequency - 1 + direction + 300) % 300) + 1;
      frequencySlider.value(frequency);
      dirty = true;
    } else if (p.key === "a") {
      autoRotate = !autoRotate;
      dirty = true;
    } else if (p.key === "A") {
      autoFrequency = !autoFrequency;
      dirty = true;
    } else if (p.key === "p" || p.key === " ") {
      pause = !pause;
    } else if (p.key === "Q") {
      // Start autosave sequence
      autoSave = true;
      p.frameRate(5);
      angle = 1; // Reset to start
      angleSlider.value(angle);
      autoSaveCount = 0;
      autoRotate = false; // Disable auto-rotate if it's on
      dirty = true;
    } else if (p.key === "S") {
      targetLayer.save(generateFilename("mona-cmyk"));
    }
  };

  const displayGrid = (p, gridSize = 1) => {
    // Ensure gridSize is at least 1
    gridSize = Math.max(1, Math.floor(gridSize));

    p.stroke(0);
    p.strokeWeight(1);

    // Draw vertical lines
    const xStep = p.width / (gridSize + 1);
    for (let i = 1; i <= gridSize; i++) {
      const x = xStep * i;
      p.line(x, 0, x, p.height);
    }

    // Draw horizontal lines
    const yStep = p.height / (gridSize + 1);
    for (let i = 1; i <= gridSize; i++) {
      const y = yStep * i;
      p.line(0, y, p.width, y);
    }
  };

  function handleFile(file) {
    if (file.type === "image") {
      img = p.loadImage(file.data, () => {
        resizeCanvasToImage();
        console.log("Image loaded successfully");
      });
    } else {
      console.log("Not an image file!");
    }
  }

  const displayUI = () => {
    const uiText = [
      `frequency: ${Math.ceil(frequency / 2)}`,
      `angle: ${angle.toFixed(1)}`,
      `image: ${img.width}x${img.height}`,
      `display: ${Math.floor(p.width)}x${Math.floor(p.height)}`,
      autoSave ? "AUTO-SAVE ON" : "",
      autoRotate ? "auto-rotate ON" : "",
      autoFrequency ? "auto-frequency ON" : "",
      pause ? "PAUSED" : "",
    ].filter(Boolean); // removes empty strings

    const boxWidth = 200;
    const boxHeight = uiText.length * 20 + 20;

    p.push();

    p.fill(0, 150);
    p.noStroke();
    p.rect(5, p.height - boxHeight - 5, boxWidth, boxHeight, 10);

    p.fill("white");
    p.textSize(16);
    p.textAlign(p.LEFT, p.TOP);
    uiText.forEach((text, index) => {
      p.text(text, 10, p.height - boxHeight + 5 + index * 20);
    });

    p.pop();
  };
};

new p5(sketch); // eslint-disable-line no-new, new-cap

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

precision mediump float;

uniform vec2 iResolution;
uniform sampler2D iChannel0;
uniform float frequency;
uniform float angle;
uniform bool cmyk_flag;
uniform float density;
varying vec2 v_texCoord;

float aastep(float threshold, float value) {
    float afwidth = frequency * (1.0 / 1000.0);
    float minval = max(threshold - afwidth, 0.0);
    float maxval = min(threshold + afwidth, 1.0);
    return smoothstep(minval, minval, value);
}

vec2 rotate2D(vec2 v, float angdegree) {
    float c = cos(radians(angdegree));
    float s = sin(radians(angdegree));
    return mat2(c, -s, s, c) * v;
}

vec4 rgb2cmyki(vec3 c) {
    float w = max(max(c.r, c.g), c.b);
    return vec4((w - c.rgb) / w, 1.0 - w);
}

vec3 cmyki2rgb(vec4 c) {
    return (1.0 - c.rgb) * (1.0 - c.a);
}

float halftoneratio(vec2 st, float col, float frequency, float angdegree) {
    st *= vec2(1.0, iResolution.y / iResolution.x);
    st -= vec2(0.5 * density);

    vec2 st2 = frequency * rotate2D(st, angdegree + angle);
    st2 += vec2(0.5 * density);

    vec2 uv = 2.0 * fract(st2) - 1.0;
    uv *= 0.9;
    float newcol = aastep(0.0, sqrt(col) - length(uv));
    return newcol;
}

void main() {
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

    vec3 texcolor = texture2D(iChannel0, v_texCoord).xyz;

    vec3 rgbscreen;

    if (cmyk_flag) {
        vec4 cmyk = rgb2cmyki(texcolor);

        float k = halftoneratio(uv, cmyk.w, frequency, 45.0);
        float c = halftoneratio(uv, cmyk.x, frequency, 15.0);
        float m = halftoneratio(uv, cmyk.y, frequency, 75.0);
        float y = halftoneratio(uv, cmyk.z, frequency, 0.0);

        rgbscreen = cmyki2rgb(vec4(c, m, y, k));
    } else {
        vec3 texcol2 = (1.0 - texcolor);
        float r = halftoneratio(uv, texcol2.r, frequency, 15.0);
        float g = halftoneratio(uv, texcol2.g, frequency, 45.0);
        float b = halftoneratio(uv, texcol2.b, frequency, 0.0);
        rgbscreen = 1.0 - vec3(r, g, b);
    }

    gl_FragColor = vec4(rgbscreen, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

precision mediump float;
uniform sampler2D u_image;
uniform vec3 u_palette[8];
uniform int u_numColors;
varying vec2 v_texCoord;

// This rgb2hsv function returns a vec3 with the following ranges:
// h: [0.0, 1.0] - Hue normalized from 0-360 degrees to 0-1 range
// s: [0.0, 1.0] - Saturation from 0-100% normalized to 0-1 range  
// v: [0.0, 1.0] - Value/brightness from 0-100% normalized to 0-1 range
vec3 rgb2hsv(vec3 c) {
  vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
  vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
  vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
  float d = q.x - min(q.w, q.y);
  float e = 1.0e-10;
  return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

void main() {
  vec4 color = texture2D(u_image, v_texCoord);
  vec3 hsv = rgb2hsv(color.rgb);
  float lum = dot(color.rgb, vec3(0.2125, 0.7154, 0.0721));
  float bg = dot(color.rgb, u_palette[0]);
  float fg = dot(color.rgb, u_palette[1]);
  vec3 hued = vec3(1.0, 1.0, 1.0);

  float step = 1.0 / float(u_numColors);
  if(hsv[0] <= step) {
    hued = u_palette[0];
  } else if(hsv[0] <= 2.0 * step) {
    hued = u_palette[1];
  } else if(hsv[0] <= 3.0 * step) {
    hued = u_palette[2];
  } else if(hsv[0] <= 4.0 * step) {
    hued = u_palette[3];
  } else if(hsv[0] <= 5.0 * step) {
    hued = u_palette[4];
  } else if(hsv[0] <= 6.0 * step) {
    hued = u_palette[5];
  } else if(hsv[0] <= 7.0 * step) {
    hued = u_palette[6];
  } else {
    hued = u_palette[7];
  }
  gl_FragColor = vec4(hued, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

precision mediump float;

// texcoords from the vertex shader
varying vec2 vTexCoord;

// our texture coming from p5
uniform sampler2D tex0;

// the size of a texel or 1.0 / width , 1.0 / height
uniform vec2 texelSize;

// which way to blur, vec2(1.0, 0.0) is horizontal, vec2(0.0, 1.0) is vertical
uniform vec2 direction;

// gaussian blur filter modified from Filip S. at intel 
// https://software.intel.com/en-us/blogs/2014/07/15/an-investigation-of-fast-real-time-gpu-based-image-blur-algorithms
// this function takes three parameters, the texture we want to blur, the uvs, and the texelSize
vec3 gaussianBlur( sampler2D t, vec2 texUV, vec2 stepSize ){   
	// a variable for our output                              
	vec3 colOut = vec3( 0.0 );

	// stepCount is 9 because we have 9 items in our array , const means that 9 will never change and is required loops in glsl
	const int stepCount = 9;

	// these weights were pulled from the link above
	float gWeights[stepCount];
	    gWeights[0] = 0.10855;
	    gWeights[1] = 0.13135;
	    gWeights[2] = 0.10406;
	    gWeights[3] = 0.07216;
	    gWeights[4] = 0.04380;
	    gWeights[5] = 0.02328;
	    gWeights[6] = 0.01083;
	    gWeights[7] = 0.00441;
	    gWeights[8] = 0.00157;

	// these offsets were also pulled from the link above
	float gOffsets[stepCount];
	    gOffsets[0] = 0.66293;
	    gOffsets[1] = 2.47904;
	    gOffsets[2] = 4.46232;
	    gOffsets[3] = 6.44568;
	    gOffsets[4] = 8.42917;
	    gOffsets[5] = 10.41281;
	    gOffsets[6] = 12.39664;
	    gOffsets[7] = 14.38070;
	    gOffsets[8] = 16.36501;

	// lets loop nine times
	for( int i = 0; i < stepCount; i++ ){  

		// multiply the texel size by the by the offset value
	    vec2 texCoordOffset = gOffsets[i] * stepSize;

		// sample to the left and to the right of the texture and add them together
	    vec3 col = texture2D( t, texUV + texCoordOffset ).xyz + texture2D( t, texUV - texCoordOffset ).xyz; 

		// multiply col by the gaussian weight value from the array
		col *= gWeights[i];

		// add it all up
	    colOut +=  col;
	}

	// our final value is returned as col out
	return colOut;
} 


void main() {

  vec2 uv = vTexCoord;
  // the texture is loaded upside down and backwards by default so lets flip it
  uv = 1.0 - uv;

  // use our blur function
  vec3 blur = gaussianBlur(tex0, uv, texelSize * direction);

  gl_FragColor = vec4(blur, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 vTexCoord;

void main() {
  // copy the texcoords
  vTexCoord = aTexCoord;
  vTexCoord.y = 1.0 - vTexCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

/* global p5 chroma themes */

// shaders converted from https://github.com/libretro/glsl-shaders/blob/master/misc/shaders/cmyk-halftone-dot.glsl

// WIP attempting to use 2 shaders

const timestamp = () => {
  const d = new Date()
  const year = d.getFullYear()
  const month = String(d.getMonth() + 1).padStart(2, '0')
  const day = String(d.getDate()).padStart(2, '0')
  const hour = String(d.getHours()).padStart(2, '0')
  const min = String(d.getMinutes()).padStart(2, '0')
  const secs = String(d.getSeconds()).padStart(2, '0')
  const millis = String(d.getMilliseconds()).padStart(3, '0')
  return `${year}${month}${day}.${hour}${min}${secs}.${millis}`
}

function generateFilename (prefix) {
  return `${prefix || 'mona'}-${timestamp()}.png`
}

const sketch = p => {
  let cmykShader
  let remapShader
  let blurShaderH
  let blurShaderV
  let posterizeShader
  let posterizeEnabled = true
  let cmykEnabled = true
  let remapEnabled = true
  let blurEnabled = true
  let blurStrength = 6.0
  let blurSlider
  let splitSlider
  let split = 6
  let img
  let cmykLayer
  let remapLayer
  let blurLayerH
  let blurLayerV
  let posterizeLayer
  let targetLayer
  let frequencySlider
  let frequency = 20.0
  let angleSlider
  let angle = 0.0
  let dirty = true
  let autoRotate = false
  let autoFrequency = false
  let autoFreqDirection = -1
  let autoRotateDirection = -1
  let pause = false
  let showCrosshairs = false
  let gridSize = 1
  let useCMYK = false
  let autoSave = false
  let autoSaveCount = 0
  let pallette
  let colorTheme
  let themeIndex = 0
  let shift = 0
  let numColors = 8 // Default number of colors
  let density = 1

  p.preload = () => {
    cmykShader = p.loadShader('2025.01.24.cmyk.vert', '2025.01.24.cmyk.frag')
    remapShader = p.loadShader('2025.01.24.remap.vert', '2025.01.24.remap.frag')
    blurShaderH = p.loadShader('2025.01.24.blur.vert', '2025.01.24.blur.frag')
    blurShaderV = p.loadShader('2025.01.24.blur.vert', '2025.01.24.blur.frag')
    posterizeShader = p.loadShader('default.vert', 'posterize.frag')
    // img = p.loadImage('images/multi-color-20250119.194439.824.png')
    // img = p.loadImage('images/mona-lisa-6195291.png')
    // img = p.loadImage('images/PXL_20240902_192356810.jpg')
    img = p.loadImage('images/mona.png')
  }

  function setHues (picked) {
    const hues = picked.hues.map(k => chroma(k).gl().slice(0, 3))

    const arranged = rotateArray(
      [
        chroma(picked.bg).gl().slice(0, 3),
        chroma(picked.fg).gl().slice(0, 3),
        ...hues
      ],
      shift
    ).slice(0, numColors) // Limit the number of hues

    // Ensure the palette has exactly 8 elements
    while (arranged.length < 8) {
      arranged.push([0.0, 0.0, 0.0]) // Fill with black or any default color
    }

    // 6 hues from theme, plus foreground and background
    pallette = new Float32Array(arranged.flat())
  }

  function rotateArray (arr, shiftAmount) {
    // Handle empty array or no shift needed
    if (!arr.length || shiftAmount === 0) return arr

    // Normalize shift amount to array length
    const shift = shiftAmount % arr.length

    // Handle negative shifts
    const normalizedShift = shift >= 0 ? shift : arr.length + shift

    // Slice and concat to create rotated array
    return arr.slice(normalizedShift).concat(arr.slice(0, normalizedShift))
  }

  const resizeCanvasToImage = () => {
    const defaultMaxSize = 3000

    // large images throw error:
    // .WebGL-0x1040cfc4700] GL_INVALID_VALUE: Negative offset.
    // fails with image @ size 3468x2611
    // fails with image @ size 3264x2458
    // [.WebGL-0x10417e17100] GL_OUT_OF_MEMORY: Error: 0x00000505, in
    // ../../third_party/angle/src/libANGLE/renderer/gl/RenderbufferGL.cpp,
    // setStorageMultisample:83. Internal error: 0x00000505: Unexpected driver error.

    // The below usually solves for the above issues
    // BUT NOT ALWAYS
    if (img.width > defaultMaxSize || img.height > defaultMaxSize) {
      let newWidth = img.width
      let newHeight = img.height

      if (newWidth > newHeight) {
        newHeight = (defaultMaxSize * newHeight) / newWidth
        newWidth = defaultMaxSize
      } else {
        newWidth = (defaultMaxSize * newWidth) / newHeight
        newHeight = defaultMaxSize
      }

      const tempG = p.createGraphics(newWidth, newHeight)
      tempG.image(img, 0, 0, newWidth, newHeight)
      img = tempG
    }

    if (!cmykLayer) {
      cmykLayer = p.createGraphics(img.width, img.height, p.WEBGL)
      cmykLayer.shader(cmykShader)
    } else {
      cmykLayer.resizeCanvas(img.width, img.height)
    }

    if (!remapLayer) {
      remapLayer = p.createGraphics(img.width, img.height, p.WEBGL)
      remapLayer.shader(remapShader)
    } else {
      remapLayer.resizeCanvas(img.width, img.height)
    }

    if (!blurLayerH) {
      blurLayerH = p.createGraphics(img.width, img.height, p.WEBGL)
      blurLayerH.shader(blurShaderH)
    } else {
      blurLayerH.resizeCanvas(img.width, img.height)
    }
    if (!blurLayerV) {
      blurLayerV = p.createGraphics(img.width, img.height, p.WEBGL)
      blurLayerV.shader(blurShaderV)
    } else {
      blurLayerV.resizeCanvas(img.width, img.height)
    }

    if(!posterizeLayer) {
      posterizeLayer = p.createGraphics(img.width, img.height, p.WEBGL)
      posterizeLayer.shader(posterizeShader)
    } else {
      posterizeLayer.resizeCanvas(img.width, img.height)
    }

    if (!targetLayer) {
      targetLayer = p.createGraphics(img.width, img.height)
    } else {
      targetLayer.resizeCanvas(img.width, img.height)
    }

    // Calculate dimensions maintaining aspect ratio
    let w = img.width
    let h = img.height
    const maxDim = 600

    if (w > maxDim || h > maxDim) {
      if (w > h) {
        h = (maxDim * h) / w
        w = maxDim
      } else {
        w = (maxDim * w) / h
        h = maxDim
      }
    }
    p.resizeCanvas(w, h)

    // Update shader resolution uniform
    cmykShader.setUniform('iResolution', [img.width, img.height])
    cmykShader.setUniform('density', density)
    // remap shader doesn't need?
    dirty = true
  }

  p.setup = () => {
    p.createCanvas(p.windowWidth, p.windowHeight).drop(handleFile)
    density = p.pixelDensity()

    resizeCanvasToImage()

    colorTheme = p.random(themes)
    setHues(colorTheme)

    frequencySlider = p.createSlider(1, 300, frequency)
    frequencySlider.position(10, 10)
    frequencySlider.attribute('title', 'frequency')
    frequencySlider.input(() => {
      frequency = frequencySlider.value()
      dirty = true
    })

    angleSlider = p.createSlider(0, 360, angle)
    angleSlider.position(10, 30)
    angleSlider.attribute('title', 'angle')
    angleSlider.input(() => {
      angle = angleSlider.value()
      dirty = true
    })

    blurSlider = p.createSlider(0.0, 20.0, blurStrength, 0.1)
    blurSlider.position(10, 50)
    blurSlider.attribute('title', 'angle')
    blurSlider.input(() => {
      blurStrength = blurSlider.value()
      dirty = true
    })

    splitSlider = p.createSlider(1, 20, split, 1)
    splitSlider.position(10, 70)
    splitSlider.attribute('title', 'blur split')
    splitSlider.input(() => {
      split = splitSlider.value()
      dirty = true
    })

    // Create radio button group
    const radioGroup = p.createRadio()
    radioGroup.position(10, 90)
    radioGroup.option('CMYK', 'CMYK')
    radioGroup.option('RGB', 'RGB')
    radioGroup.selected('CMYK')
    radioGroup.style('width', '200px')
    radioGroup.changed(() => {
      useCMYK = radioGroup.value() === 'CMYK'
      dirty = true
    })
  }

  // more complicated values for autoRotate and autoFrequency

  p.draw = () => {
    if (!pause) {
      if (autoSave) {
        if (angle >= 360) {
          autoSave = false // Stop when we reach 360
          dirty = true
          p.frameRate(60)
          return
        }

        targetLayer.save(
          generateFilename(
            `mona-cmyk_${String(autoSaveCount).padStart(4, '0')}`
          )
        )
        angle += 5 // Increment by 5 degrees
        angleSlider.value(angle)
        autoSaveCount++
        dirty = true
      } else if (autoRotate) {
        // atm, I prefer angle t contiously spin
        // if (angle <= 0 || angle >= 360) {
        //   autoRotateDirection *= -1
        // }
        angle = (angle + 1) % 360
        angleSlider.value(angle)
        dirty = true
      }
      if (autoFrequency) {
        if (frequency <= 1 || frequency >= 150) {
          autoFreqDirection *= -1
        }
        frequency = frequency + autoFreqDirection
        frequencySlider.value(frequency)
        dirty = true
      }
    }

    if (!dirty) return
    let currentLayer = img // Start with the original image

    if (blurEnabled) {
      // I found that blur works better for me
      // at lower values repeated multiple times
      // this might not be a _standard_ usage
      // but works for what I want
      for(let i = 0; i < split; i++) {
      blurShaderH.setUniform('tex0', currentLayer)
      blurShaderH.setUniform('strength', blurStrength / split)
      blurShaderH.setUniform('texelSize', [
        1.0 / currentLayer.width,
        1.0 / currentLayer.height
      ])
      blurShaderH.setUniform('direction', [1.0, 1.0])
      blurLayerH.rect(0, 0, blurLayerH.width, blurLayerH.height)

      blurShaderV.setUniform('tex0', blurLayerH)
      blurShaderV.setUniform('strength', blurStrength / split)
      blurShaderV.setUniform('texelSize', [
        1.0 / currentLayer.width,
        1.0 / currentLayer.height
      ])
      blurShaderV.setUniform('direction', [1.0, 1.0])
      blurLayerV.rect(0, 0, blurLayerV.width, blurLayerV.height)

      currentLayer = blurLayerV
      }
    }

    if (posterizeEnabled) {
      posterizeShader.setUniform('u_image', currentLayer)
      posterizeShader.setUniform('levels', numColors)
      posterizeLayer.rect(0, 0, posterizeLayer.width, posterizeLayer.height)
      currentLayer = posterizeLayer
    }

    if (remapEnabled) {
      remapShader.setUniform('u_image', currentLayer)
      remapShader.setUniform('u_palette', pallette)
      remapShader.setUniform('u_numColors', numColors)
      remapLayer.rect(0, 0, remapLayer.width, remapLayer.height)
      currentLayer = remapLayer
    }

    if (cmykEnabled) {
      cmykShader.setUniform('iChannel0', currentLayer)
      cmykShader.setUniform('frequency', frequency)
      cmykShader.setUniform('angle', angle)
      cmykShader.setUniform('cmyk_flag', useCMYK)
      cmykLayer.rect(0, 0, cmykLayer.width, cmykLayer.height)
      currentLayer = cmykLayer
    }

    targetLayer.image(currentLayer, 0, 0, targetLayer.width, targetLayer.height)

    p.clear()
    p.image(targetLayer, 0, 0, p.width, p.height)
    if (showCrosshairs) {
      displayGrid(p, gridSize)
    }
    displayUI()
    dirty = false
  }

  p.keyPressed = () => {
    if (p.key === '1') {
      cmykEnabled = !cmykEnabled
      dirty = true
    } else if (p.key === '!') {
      remapEnabled = !remapEnabled
      dirty = true
    } else if (p.key === '@') {
      blurEnabled = !blurEnabled
      dirty = true
    } else if (p.key === '#') {
      posterizeEnabled = !posterizeEnabled
      dirty = true
    } else if (p.key === 'z') {
      // TODO: find a better key
      colorTheme = p.random(themes)
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'h') {
      shift -= 1
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'l') {
      shift += 1
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'j') {
      themeIndex = (themeIndex - 1 + themes.length) % themes.length
      colorTheme = themes[themeIndex]
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'k') {
      themeIndex = (themeIndex + 1) % themes.length
      colorTheme = themes[themeIndex]
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'r') {
      shift = 0
      setHues(colorTheme)
      dirty = true
    } else if ('2345678'.indexOf(p.key) !== -1) {
      numColors = Number.parseInt(p.key, 10)
      setHues(colorTheme)
      dirty = true
    } else if (p.key === 'c') {
      showCrosshairs = !showCrosshairs
      dirty = true
    } else if (showCrosshairs && p.key === ']') {
      gridSize = Math.min(10, gridSize + 1) // Limit to 10 for example
      dirty = true
    } else if (showCrosshairs && p.key === '[') {
      gridSize = Math.max(1, gridSize - 1)
      dirty = true
    } else if ((!autoRotate || pause) && (p.key === 'r' || p.key === 'R')) {
      const direction = p.key === 'r' ? 1 : -1
      angle = (angle + direction) % 360
      angleSlider.value(angle)
      dirty = true
    } else if (!(autoFrequency || pause) && (p.key === 'f' || p.key === 'F')) {
      const direction = p.key === 'f' ? 1 : -1
      frequency = ((frequency - 1 + direction + 300) % 300) + 1
      frequencySlider.value(frequency)
      dirty = true
    } else if (p.key === 'a') {
      autoRotate = !autoRotate
      dirty = true
    } else if (p.key === 'A') {
      autoFrequency = !autoFrequency
      dirty = true
    } else if (p.key === 'p' || p.key === ' ') {
      pause = !pause
    } else if (p.key === 'Q') {
      // Start autosave sequence
      autoSave = true
      p.frameRate(5)
      angle = 1 // Reset to start
      angleSlider.value(angle)
      autoSaveCount = 0
      autoRotate = false // Disable auto-rotate if it's on
      dirty = true
    } else if (p.key === 'S') {
      targetLayer.save(generateFilename('mona-cmyk'))
    }
  }

  const displayGrid = (p, gridSize = 1) => {
    // Ensure gridSize is at least 1
    gridSize = Math.max(1, Math.floor(gridSize))

    p.stroke(0)
    p.strokeWeight(1)

    // Draw vertical lines
    const xStep = p.width / (gridSize + 1)
    for (let i = 1; i <= gridSize; i++) {
      const x = xStep * i
      p.line(x, 0, x, p.height)
    }

    // Draw horizontal lines
    const yStep = p.height / (gridSize + 1)
    for (let i = 1; i <= gridSize; i++) {
      const y = yStep * i
      p.line(0, y, p.width, y)
    }
  }

  function handleFile (file) {
    if (file.type === 'image') {
      img = p.loadImage(file.data, () => {
        resizeCanvasToImage()
        console.log('Image loaded successfully')
      })
    } else {
      console.log('Not an image file!')
    }
  }

  const displayUI = () => {
    const uiText = [
      `frequency: ${Math.ceil(frequency / 2)}`,
      `angle: ${angle.toFixed(1)}`,
      blurEnabled ? `blur: ${blurStrength.toFixed(1)}` : '',
      blurEnabled ? `blur split: ${split}` : '',
      `image: ${img.width}x${img.height}`,
      `display: ${p.width}x${p.height}`,
      posterizeEnabled ? 'posterize' : '',
      autoSave ? 'AUTO-SAVE ON' : '',
      autoRotate ? 'auto-rotate ON' : '',
      autoFrequency ? 'auto-frequency ON' : '',
      pause ? 'PAUSED' : ''
    ].filter(Boolean) // removes empty strings

    const boxWidth = 200
    const boxHeight = uiText.length * 20 + 20

    p.push()

    p.fill(0, 150)
    p.noStroke()
    p.rect(5, p.height - boxHeight - 5, boxWidth, boxHeight, 10)

    p.fill('white')
    p.textSize(16)
    p.textAlign(p.LEFT, p.TOP)
    uiText.forEach((text, index) => {
      p.text(text, 10, p.height - boxHeight + 5 + index * 20)
    })

    p.pop()
  }
}

new p5(sketch) // eslint-disable-line no-new, new-cap

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

precision mediump float;

uniform vec2 iResolution;
uniform sampler2D iChannel0;
uniform float frequency;
uniform float angle;
uniform bool cmyk_flag;
uniform float density;
varying vec2 v_texCoord;

float aastep(float threshold, float value) {
    float afwidth = frequency * (1.0 / 500.0);
    float minval = max(threshold - afwidth, 0.0);
    float maxval = min(threshold + afwidth, 1.0);
    return smoothstep(minval, minval, value);
}

vec2 rotate2D(vec2 v, float angdegree) {
    float c = cos(radians(angdegree));
    float s = sin(radians(angdegree));
    return mat2(c, -s, s, c) * v;
}

vec4 rgb2cmyki(vec3 c) {
    float w = max(max(c.r, c.g), c.b);
    return vec4((w - c.rgb) / w, 1.0 - w);
}

vec3 cmyki2rgb(vec4 c) {
    return (1.0 - c.rgb) * (1.0 - c.a);
}

float halftoneratio(vec2 st, float col, float frequency, float angdegree) {
    st *= vec2(1.0, iResolution.y / iResolution.x);
    st -= vec2(0.5 * density);

    vec2 st2 = frequency * rotate2D(st, angdegree + angle);
    st2 += vec2(0.5 * density);

    vec2 uv = 2.0 * fract(st2) - 1.0;
    uv *= 0.9;
    float newcol = aastep(0.0, sqrt(col) - length(uv));
    return newcol;
}

void main() {
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

    vec3 texcolor = texture2D(iChannel0, v_texCoord).xyz;

    vec3 rgbscreen;

    if (cmyk_flag) {
        vec4 cmyk = rgb2cmyki(texcolor);

        float k = halftoneratio(uv, cmyk.w, frequency, 45.0);
        float c = halftoneratio(uv, cmyk.x, frequency, 15.0);
        float m = halftoneratio(uv, cmyk.y, frequency, 75.0);
        float y = halftoneratio(uv, cmyk.z, frequency, 0.0);

        rgbscreen = cmyki2rgb(vec4(c, m, y, k));
    } else {
        vec3 texcol2 = (1.0 - texcolor);
        float r = halftoneratio(uv, texcol2.r, frequency, 15.0);
        float g = halftoneratio(uv, texcol2.g, frequency, 45.0);
        float b = halftoneratio(uv, texcol2.b, frequency, 0.0);
        rgbscreen = 1.0 - vec3(r, g, b);
    }

    gl_FragColor = vec4(rgbscreen, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

precision mediump float;
uniform sampler2D u_image;
uniform vec3 u_palette[8];
uniform int u_numColors;
varying vec2 v_texCoord;

// This rgb2hsv function returns a vec3 with the following ranges:
// h: [0.0, 1.0] - Hue normalized from 0-360 degrees to 0-1 range
// s: [0.0, 1.0] - Saturation from 0-100% normalized to 0-1 range  
// v: [0.0, 1.0] - Value/brightness from 0-100% normalized to 0-1 range
vec3 rgb2hsv(vec3 c) {
  vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
  vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
  vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
  float d = q.x - min(q.w, q.y);
  float e = 1.0e-10;
  return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

void main() {
  vec4 color = texture2D(u_image, v_texCoord);
  vec3 hsv = rgb2hsv(color.rgb);
  float lum = dot(color.rgb, vec3(0.2125, 0.7154, 0.0721));
  vec3 hued = vec3(1.0, 1.0, 1.0);

  float step = 1.0 / float(u_numColors);
  if(hsv[0] <= step) {
    hued = u_palette[0];
  } else if(hsv[0] <= 2.0 * step) {
    hued = u_palette[1];
  } else if(hsv[0] <= 3.0 * step) {
    hued = u_palette[2];
  } else if(hsv[0] <= 4.0 * step) {
    hued = u_palette[3];
  } else if(hsv[0] <= 5.0 * step) {
    hued = u_palette[4];
  } else if(hsv[0] <= 6.0 * step) {
    hued = u_palette[5];
  } else if(hsv[0] <= 7.0 * step) {
    hued = u_palette[6];
  } else {
    hued = u_palette[7];
  }
  gl_FragColor = vec4(hued, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

precision mediump float;

// texcoords from the vertex shader
varying vec2 vTexCoord;

// our texture coming from p5
uniform sampler2D tex0;

// the size of a texel or 1.0 / width , 1.0 / height
uniform vec2 texelSize;

// which way to blur, vec2(1.0, 0.0) is horizontal, vec2(0.0, 1.0) is vertical
uniform vec2 direction;

uniform float strength;

// gaussian blur filter modified from Filip S. at intel 
// https://software.intel.com/en-us/blogs/2014/07/15/an-investigation-of-fast-real-time-gpu-based-image-blur-algorithms
// this function takes three parameters, the texture we want to blur, the uvs, and the texelSize
vec3 gaussianBlur( sampler2D t, vec2 texUV, vec2 stepSize ){   
	// a variable for our output                              
	vec3 colOut = vec3( 0.0 );

	// stepCount is 9 because we have 9 items in our array , const means that 9 will never change and is required loops in glsl
	const int stepCount = 9;

	// these weights were pulled from the link above
	float gWeights[stepCount];
	    gWeights[0] = 0.10855;
	    gWeights[1] = 0.13135;
	    gWeights[2] = 0.10406;
	    gWeights[3] = 0.07216;
	    gWeights[4] = 0.04380;
	    gWeights[5] = 0.02328;
	    gWeights[6] = 0.01083;
	    gWeights[7] = 0.00441;
	    gWeights[8] = 0.00157;

	// these offsets were also pulled from the link above
	float gOffsets[stepCount];
	    gOffsets[0] = 0.66293;
	    gOffsets[1] = 2.47904;
	    gOffsets[2] = 4.46232;
	    gOffsets[3] = 6.44568;
	    gOffsets[4] = 8.42917;
	    gOffsets[5] = 10.41281;
	    gOffsets[6] = 12.39664;
	    gOffsets[7] = 14.38070;
	    gOffsets[8] = 16.36501;

	// lets loop nine times
	for( int i = 0; i < stepCount; i++ ){  

		// multiply the texel size by the by the offset value
	    vec2 texCoordOffset = gOffsets[i] * stepSize;

		// sample to the left and to the right of the texture and add them together
	    vec3 col = texture2D( t, texUV + texCoordOffset ).xyz + texture2D( t, texUV - texCoordOffset ).xyz; 

		// multiply col by the gaussian weight value from the array
		col *= gWeights[i];

		// add it all up
	    colOut +=  col;
	}

	// our final value is returned as col out
	return colOut;
} 


void main() {

  vec2 uv = vTexCoord;
  // the texture is loaded upside down and backwards by default so lets flip it
  uv = 1.0 - uv;

  // use our blur function
  vec3 blur = gaussianBlur(tex0, uv, texelSize * direction * strength);

  gl_FragColor = vec4(blur, 1.0);
}

