  loopButtToggle = !loopButtToggle;
  if (loopButtToggle) {
    loopText.html('Looping');
  } else {
    loopText.html('Paused');
  }
}

class dot {
  constructor(_x, _y, _r32, _r24, _r35, _r33, _R, _G, _B) {
    this.x = _x;
    this.y = _y;
    this.l32r = _r32;
    this.l24r = _r24;
    this.l35r = _r35;
    this.l33r = _r33;
    this.R = _R;
    this.G = _G;
    this.B = _B;
  }
   morph(r) {
    fill(this.R, this.G, this.B, a);
    ellipse(this.x, this.y, s * r);
  }
  // morph(a, r, s) {
