function mouseDragged() {
  if (drawing == 1) {
    if (fromP1.x != gridify(mouseX) || fromP1.y != gridify(mouseY)) {
      fromP2.set(gridify(mouseX), gridify(mouseY));
    }
  } else if (drawing == 2) {
    toP2.set(gridify(mouseX), gridify(mouseY));
    drawing++;
  } else if (drawing == 3) {
    toP2.set(gridify(mouseX), gridify(mouseY));
  }
