precision mediump float;

uniform vec2 iResolution;
uniform sampler2D iChannel0;
uniform float frequency;
uniform float angle;
uniform bool cmyk_flag;
uniform float density;
varying vec2 v_texCoord;

float aastep(float threshold, float value) {
    float afwidth = frequency * (1.0 / 1000.0);
    float minval = max(threshold - afwidth, 0.0);
    float maxval = min(threshold + afwidth, 1.0);
    return smoothstep(minval, minval, value);
}

vec2 rotate2D(vec2 v, float angdegree) {
    float c = cos(radians(angdegree));
    float s = sin(radians(angdegree));
    return mat2(c, -s, s, c) * v;
}

vec4 rgb2cmyki(vec3 c) {
    float w = max(max(c.r, c.g), c.b);
    return vec4((w - c.rgb) / w, 1.0 - w);
}

vec3 cmyki2rgb(vec4 c) {
    return (1.0 - c.rgb) * (1.0 - c.a);
}

float halftoneratio(vec2 st, float col, float frequency, float angdegree) {
    st *= vec2(1.0, iResolution.y / iResolution.x);
    st -= vec2(0.5 * density);

    vec2 st2 = frequency * rotate2D(st, angdegree + angle);
    st2 += vec2(0.5 * density);

    vec2 uv = 2.0 * fract(st2) - 1.0;
    uv *= 0.9;
    float newcol = aastep(0.0, sqrt(col) - length(uv));
    return newcol;
}

void main() {
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

    vec3 texcolor = texture2D(iChannel0, v_texCoord).xyz;

    vec3 rgbscreen;

    if (cmyk_flag) {
        vec4 cmyk = rgb2cmyki(texcolor);

        float k = halftoneratio(uv, cmyk.w, frequency, 45.0);
        float c = halftoneratio(uv, cmyk.x, frequency, 15.0);
        float m = halftoneratio(uv, cmyk.y, frequency, 75.0);
        float y = halftoneratio(uv, cmyk.z, frequency, 0.0);

        rgbscreen = cmyki2rgb(vec4(c, m, y, k));
    } else {
        vec3 texcol2 = (1.0 - texcolor);
        float r = halftoneratio(uv, texcol2.r, frequency, 15.0);
        float g = halftoneratio(uv, texcol2.g, frequency, 45.0);
        float b = halftoneratio(uv, texcol2.b, frequency, 0.0);
        rgbscreen = 1.0 - vec3(r, g, b);
    }

    gl_FragColor = vec4(rgbscreen, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

precision mediump float;
uniform sampler2D u_image;
uniform vec3 u_palette[8];
uniform int u_numColors;
varying vec2 v_texCoord;

// This rgb2hsv function returns a vec3 with the following ranges:
// h: [0.0, 1.0] - Hue normalized from 0-360 degrees to 0-1 range
// s: [0.0, 1.0] - Saturation from 0-100% normalized to 0-1 range  
// v: [0.0, 1.0] - Value/brightness from 0-100% normalized to 0-1 range
vec3 rgb2hsv(vec3 c) {
  vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
  vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
  vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
  float d = q.x - min(q.w, q.y);
  float e = 1.0e-10;
  return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

void main() {
  vec4 color = texture2D(u_image, v_texCoord);
  vec3 hsv = rgb2hsv(color.rgb);
  float lum = dot(color.rgb, vec3(0.2125, 0.7154, 0.0721));
  float bg = dot(color.rgb, u_palette[0]);
  float fg = dot(color.rgb, u_palette[1]);
  vec3 hued = vec3(1.0, 1.0, 1.0);

  float step = 1.0 / float(u_numColors);
  if(hsv[0] <= step) {
    hued = u_palette[0];
  } else if(hsv[0] <= 2.0 * step) {
    hued = u_palette[1];
  } else if(hsv[0] <= 3.0 * step) {
    hued = u_palette[2];
  } else if(hsv[0] <= 4.0 * step) {
    hued = u_palette[3];
  } else if(hsv[0] <= 5.0 * step) {
    hued = u_palette[4];
  } else if(hsv[0] <= 6.0 * step) {
    hued = u_palette[5];
  } else if(hsv[0] <= 7.0 * step) {
    hued = u_palette[6];
  } else {
    hued = u_palette[7];
  }
  gl_FragColor = vec4(hued, 1.0);
}

// our vertex data
attribute vec3 aPosition;
attribute vec2 aTexCoord;

// lets get texcoords just for fun! 
varying vec2 v_texCoord;

void main() {
  // copy the texcoords
  v_texCoord = aTexCoord;
  v_texCoord.y = 1.0 - v_texCoord.y;

  // copy the position data into a vec4, using 1.0 as the w component
  vec4 positionVec4 = vec4(aPosition, 1.0);
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0;

  // send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

/**
   this example aims to show the simplest hello world code possible
   for marching.
*/

function setup() {
  // put setup code here
  createCanvas(500, 500);

  background(0);
  fill(255);
  noStroke();
  ellipse(200, 200, 200, 200);
  ellipse(300, 300, 300, 40);

  loadPixels();
  const d = pixelDensity();

  //construct a 2d numeric array of pixel brightnesses, row by row
  let pixelBrightness = [];
  for (let y = 0; y < height; y++) {
    let xRow = [];
    for (let x = 0; x < width; x++) {
      const i = 4 * d * (y * d * width + x);
      const [r, g, b] = [pixels[i], pixels[i + 1], pixels[i + 2]]; // get colors
      xRow.push(brightness(color(r, g, b)));
    }
    pixelBrightness.push(xRow);
  }

  //call and draw marching vectors
  stroke(0, 255, 255);
  noFill();
  let lines = marchingSquares(pixelBrightness);
  lines.map(function (a) {
    line(a[0], a[1], a[2], a[3]);
  });
  console.log('done!')
}

