class P5JsSettings {
  static applySettings(newSettings){
    this.setSeed(this.optionsSet.settings.seed);
    noiseDetail(this.optionsSet.settings.noise_octaves, this.optionsSet.settings.noise_falloff);
  }

  static optionsMetadata() {
    return [
      { name: "seed", type: "integer", default: Math.round(random(1000))}, 
      { name: "noise_octaves", type: "integer", default: 10}, 
      { name: "noise_falloff", type: "float", default: 0.6}
    ];
  }

  static init(){
    this.optionsSet = new OptionsSet(this.optionsMetadata());
    this.applySettings(this.optionsSet.settings);
    this.logSettings();
  }

  static logSettings(){
    console.log("P5JS Settings: ");
    console.log(this.optionsSet.settings);
  }

  static addDatGui(datGuiParams){
    datGuiParams = datGuiParams || {};
    const datGui = new dat.gui.GUI(datGuiParams);

    if (datGuiParams.autoPlace === false) {
      this.datGuiContainer = this.createDatGuiContainer();
      this.datGuiContainer.appendChild(datGui.domElement);

      document.addEventListener('keyup', function(event){
        if (event.key === 'h') {
          P5JsSettings.toggleDatGuiHide();
        }
      });
    }
    return datGui;
  }

  static toggleDatGuiHide(){
    if (this.datGuiContainer.style.display === "none") {
      this.datGuiContainer.style.display = "block";
    } else {
      this.datGuiContainer.style.display = "none";
    }
  }

  static createDatGuiContainer(){
    let container = document.createElement("div"); 
    container.setAttribute('id', 'datGuiContainer');
    container.style.position = 'absolute';
    container.style.right = '0px';
    container.style.bottom = '20px';

    let label = document.createElement('div');
    label.style.color = 'white';
    label.style.backgroundColor = 'black';
    label.style.padding = '5px';
    label.style.font = "11px 'Lucida Grande',sans-serif";
    label.innerHTML = "Config (Press H to Hide/Show)";
    container.appendChild(label);

    document.body.appendChild(container);
    return container;
  }

  static setSeed(seed){
    this.optionsSet.settings.seed = seed;
    randomSeed(this.optionsSet.settings.seed);
    noiseSeed(this.optionsSet.settings.seed);
  }

  static getSeed(){ return this.optionsSet.settings.seed; }
}

class OptionsSet {
  constructor(optionsMeta){
    this.optionsMeta = optionsMeta;
    this.settingsHash = {};
    this.init();
  }

  get optionsMetadata(){
    return this.optionsMeta;
  }

  defaultSettings(){
    let defaultValues = {};
    for (let option of this.optionsMetadata) {
      defaultValues[option.name] = option.default;
    }
    return defaultValues;
  }

  readParamOptions(){
    let paramOptions = {};
    for (let option of this.optionsMetadata) {
      if (this.isArrayType(option.type)){
        paramOptions[option.name] = this.getArray(option);
      } else {
        paramOptions[option.name] = UtilFunctions.getParameterByName(option.name, this.formatterForType(option.type));
      }
    }
    return paramOptions;
  }

  formatterForType(type){
    return ({
      "integer" : (parseInt),
      "float" : (Number),
      "string" :  null,
      "bool" :  ((v) => { return (v == "true"); })
    })[type];
  }

  isArrayType(type){
    return type.startsWith("array");
  }

  getArray(option){
    let full_param = UtilFunctions.getParameterByName(option.name);
    if (!full_param) return null;

    let results = full_param.split(option.delimiter || ",");

    let type = option.type.split(":")[1];
    let formatter = this.formatterForType(type);
    if (formatter) {
      results = results.map(val => formatter(val));
    }
    return results;
  }

  init(){
    let paramOptions = this.readParamOptions();
    UtilFunctions.unsetUndefineds(paramOptions);

    Object.assign(this.settingsHash, this.defaultSettings(), paramOptions);
  }

  get settings(){
    return this.settingsHash;
  }

  logSettings(){
    console.log("Settings: ");
    console.log(this.settingsHash);
  }
}

const p5js_utils =
{
  isPaused: false
};

class P5JsUtils {
  static get UP(){    return 0; }
  static get RIGHT(){ return 1; }
  static get DOWN(){  return 2; }
  static get LEFT(){  return 3; }

  static colorAt(x, y, width){ 
    return P5JsUtils.colorAtInPixels(x, y, width, pixels);
  }

  static colorAtInPixels(x, y, width, pixels){
    let baseIdx = (round(x) + round(y) * width) * 4;
    return color(
              pixels[baseIdx + 0],
              pixels[baseIdx + 1],
              pixels[baseIdx + 2],
              pixels[baseIdx + 3]);
  }

  static get mousePoint(){
    return {x: mouseX, y: mouseY};
  }

  static colorsMatch(c1, c2){
    return      c1.levels[0] == c2.levels[0]
             && c1.levels[1] == c2.levels[1]
             && c1.levels[2] == c2.levels[2]
             && alpha(c1) == alpha(c2);
  }


  static get COLOR_ID_RED(){ return 0; }
  static get COLOR_ID_GREEN(){ return 1; }
  static get COLOR_ID_BLUE(){ return 2; }
  static get COLOR_ID_ORANGE(){ return 3; }
  static get COLOR_ID_YELLOW(){ return 4; }
  static get COLOR_ID_VIOLET(){ return 5; }

  static getRandomColorByID(colorId){
    switch(colorId) {
      case P5JsUtils.COLOR_ID_RED:    return P5JsUtils.getRandomRed();
      case P5JsUtils.COLOR_ID_GREEN:  return P5JsUtils.getRandomGreen();
      case P5JsUtils.COLOR_ID_BLUE:   return P5JsUtils.getRandomBlue();
      case P5JsUtils.COLOR_ID_ORANGE: return P5JsUtils.getRandomOrange();
      case P5JsUtils.COLOR_ID_YELLOW: return P5JsUtils.getRandomYellow();
      case P5JsUtils.COLOR_ID_VIOLET: return P5JsUtils.getRandomViolet();
      default: return P5JsUtils.getRandomColor();
    }
  }

  static getRandomRed(){
    return P5JsUtils.getRandomColor(155, 255, 1, 0, 0);
  }
  static getRandomGreen(){
    return P5JsUtils.getRandomColor(155, 255, 0, 1, 0);
  }
  static getRandomBlue(){
    return P5JsUtils.getRandomColor(155, 255, 0, 0, 1);
  }
  static getRandomOrange(){
    return P5JsUtils.getRandomColor(155, 255, 1, 0.5, 0);
  }
  static getRandomYellow(){
    return P5JsUtils.getRandomColor(155, 255, 1, 1, 0);
  }
  static getRandomViolet(){
    return P5JsUtils.getRandomColor(155, 255, 1, 0, 1);
  }

  static getRandomColor(minVal, maxVal, redFactor, greenFactor, blueFactor){
    minVal = minVal || 150;
    maxVal = maxVal || 255;
    if (redFactor == undefined) { redFactor = random(); }
    if (greenFactor == undefined) { greenFactor = random(); }
    if (blueFactor == undefined) { blueFactor = random(); }

    let baseVal = random(minVal, maxVal);
    let secondaryVal = baseVal * (0.1 + random(0, 0.5));

    let redLvl   = baseVal * redFactor + secondaryVal * (1 - redFactor);
    let greenLvl = baseVal * greenFactor + secondaryVal * (1 - greenFactor);
    let blueLvl  = baseVal * blueFactor + secondaryVal * (1 - blueFactor);
    return color(redLvl, greenLvl, blueLvl);
  }

  static toggleLoop(){
    if (p5js_utils.isPaused){
      loop();
      p5js_utils.isPaused = false;
    }else{
      noLoop();
      p5js_utils.isPaused = true;
    }
  }

  static drawSolidBoundary(fromX, fromY, toX, toY, options = {}){
    line(fromX, fromY, toX, toY);
    const dashesAbove = options['dashes_above'] === true;

    push();
    translate(fromX, fromY);
    if (fromY != toY){
      // rotate ... but currently assumes a horizontal line
    }

    const dashWidth = 20;
    const numDashes = Math.floor((toX - fromX) / dashWidth);
    let curX = 0;
    let startY  = dashesAbove ? 0 : dashWidth;
    let endY    = dashesAbove ? - dashWidth : 0;

    for (var i = 0; i<numDashes; i++){
      line (curX, startY, curX + dashWidth, endY);
      curX += dashWidth;
    }
    pop();
  }

  static drawRect(rectObj){
    rect(rectObj.x, rectObj.y, rectObj.width, rectObj.height);
  }

  static rotationBetweenVectors(v1, v2){
    let h1 = v1.heading();
    let h2 = v2.copy().rotate(-h1);
    return h2.heading();
  }

  static drawArrow(from, to){
    line(from.x, from.y, to.x, to.y);

    let segmentVector = createVector(to.x - from.x, to.y - from.y);
    let arrowVector = segmentVector.copy().setMag(8).rotate(0.1 * PI);

    line(to.x, to.y, to.x - arrowVector.x, to.y - arrowVector.y);
  }

  // Intent is to return a p5.Image from the 'file' input of
  // the canvas.drop(...) callback.
  static p5ImageFromFile(file){
    // async call, should have callback
    const img = createImg(file.data).hide();
    const gBuffer = createGraphics(img.width, img.height);

    gBuffer.image(img, 0, 0, img.width, img.height);
    gBuffer.loadPixels();

    const resultImage = createImage(img.width, img.height);
    resultImage.loadPixels();
    gBuffer.pixels.forEach((val, idx) => {
      resultImage.pixels[idx] = gBuffer.pixels[idx];
    });
    resultImage.updatePixels();
    return resultImage;
  }

  // Primary approach of temp canvas and copying over from:
  //   https://stackoverflow.com/a/32257161
  static saveCanvasArea(p5Canvas, rectArea){
    let fromCanvas = document.getElementById(p5Canvas.id());
    let tmpCanvas = document.createElement("canvas");

    tmpCanvas.style.display = 'none';
    document.body.appendChild(tmpCanvas);
    tmpCanvas.width = rectArea.width;
    tmpCanvas.height = rectArea.height;

    var tmpCtx = tmpCanvas.getContext('2d');
    tmpCtx.drawImage(
        fromCanvas, 
        rectArea.x, rectArea.y, rectArea.width, rectArea.height,
        0,0, tmpCanvas.width, tmpCanvas.height
    );

    let filename  = "screenshot-partial";
    let extension = 'png';
    let mimeType  = 'image/png';

    // following block from: p5js.saveCanvas
    // https://github.com/processing/p5.js/blob/da8abf57c79a99736e3c6ccb8146abc115f3d84a/src/image/image.js#L190-L192
    tmpCanvas.toBlob(function(blob) {
      p5.prototype.downloadFile(blob, filename, extension);
    }, mimeType);

    tmpCanvas.parentNode.removeChild(tmpCanvas);
  }

  static applyStyleSet(styleSet, buffer = undefined){
    if (buffer) {
      if (styleSet.fillColor) { buffer.fill(styleSet.fillColor); }
      if (styleSet.noFill) { buffer.noFill(); }
      if (styleSet.strokeColor) { buffer.stroke(styleSet.strokeColor); }
      if (styleSet.noStroke) { buffer.noStroke(); }
      if (styleSet.strokeWeight) { buffer.strokeWeight(styleSet.strokeWeight); }

    } else {
      if (styleSet.fillColor) { fill(styleSet.fillColor); }
      if (styleSet.noFill) { noFill(); }
      if (styleSet.strokeColor) { stroke(styleSet.strokeColor); }
      if (styleSet.noStroke) { noStroke(); }
      if (styleSet.strokeWeight) { strokeWeight(styleSet.strokeWeight); }
    }
  }

  static drawControlPoints(points){
    push();
    points.forEach(p => P5JsUtils._drawControlPoint(p));
    pop();
  }

  static _drawControlPoint(point){
    if (point.containsXY(mouseX, mouseY)){
      fill(200, 200, 100);
    }else {
      fill(100, 200, 100);
    }
    noStroke();
    ellipse(point.x, point.y, point.radius, point.radius);
  }
}

class UtilFunctions {
    // via: https://stackoverflow.com/a/901144
  static getParameterByName(name, formatter, url = null) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, "\\$&");
    let regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)");
    let results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return null;

    let value = decodeURIComponent(results[2].replace(/\+/g, " "));
    if (formatter) return formatter(value);
    return value;
  }

  // via: https://stackoverflow.com/a/38340730
  static unsetUndefineds(obj) {
    Object.keys(obj).forEach((key) => (obj[key] == null) && delete obj[key]);
    return obj;
  }

  static dist(x1, y1, x2, y2){
    return Math.sqrt( Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2) );
  }

  static shallowCopy(obj){
    return Object.assign({}, obj);
  }

  static averageOfArray(arr){
    return arr.reduce( (el, tally) => el + tally, 0) / arr.length;
  }

  // via: https://en.wikipedia.org/wiki/HSL_and_HSV#From_HSV
  static hsbToRGB(hsb){
    let C = hsb.b * hsb.s;
    let X = C * (1 - Math.abs( (hsb.h / 60) % 2 - 1));
    let m = hsb.b - C;

    let rgbPrime;

    if (hsb.h >= 0 && hsb.h < 60) {
      rgbPrime = [C, X, 0];
    } else if (hsb.h >= 60 && hsb.h < 120) {
      rgbPrime = [X, C, 0];
    } else if (hsb.h >= 120 && hsb.h < 180) {
      rgbPrime = [0, C, X];
    } else if (hsb.h >= 180 && hsb.h < 240) {
      rgbPrime = [0, X, C];
    } else if (hsb.h >= 240 && hsb.h < 300) {
      rgbPrime = [X, 0, C];
    } else if (hsb.h >= 300 && hsb.h < 360) {
      rgbPrime = [C, 0, X];
    }

    let rgb = {};
    rgb.r = (rgbPrime[0] + m) * 255;
    rgb.g = (rgbPrime[1] + m) * 255;
    rgb.b = (rgbPrime[2] + m) * 255;
    return rgb;
  }

  // via: https://en.wikipedia.org/wiki/HSL_and_HSV#HSV_to_RGB_alternative
  static hsbToRgb2(hsb){
    return {r: UtilFunctions._hsbToRgbHelper(5, hsb),
            g: UtilFunctions._hsbToRgbHelper(3, hsb),
            b: UtilFunctions._hsbToRgbHelper(1, hsb)};
  }

  static _hsbToRgbHelper(n, hsb){
    let k = (n + hsb.h / 60) % 6;
    return hsb.b - hsb.b * hsb.s * Math.max(0, Math.min(k, 4 - k, 1));
  }

  static startPerfTime(){
    this.perfTimings = this.perfTimings || [];
    this.perfStartAt = performance.now();
  }

  static endPerfTime(rollingCount = 10, logEveryNTimes = 5){
    var t1 = performance.now();
    this.perfTimings.push((t1 - this.perfStartAt));

    if (this.perfTimings.length >= rollingCount){
      console.log(`Average run: ${UtilFunctions.averageOfArray(this.perfTimings)}`);
      this.perfTimings.splice(0, logEveryNTimes);
    }
  }

  static ceil(value, increment = 1){
    return Math.ceil(value / increment) * increment;
  }

  static floor(value, increment = 1){
    return Math.floor(value / increment) * increment;
  }

  static round(value, increment = 1){
    return Math.round(value / increment) * increment;
  }
}

class Triangle {
  constructor (x1, y1, x2, y2, x3, y3){
    this.p1 = new Point(x1, y1);
    this.p2 = new Point(x2, y2);
    this.p3 = new Point(x3, y3);

    this.dragEnabled = false;
    this.isDragged = false;

    this.points = [this.p1, this.p2, this.p3];
  }

  handleMousePressed(){
    const pointPressed = this.points.find(p => p.containsXY(mouseX, mouseY));

    if (pointPressed){
      pointPressed.isBeingDragged = true;
      this.isDragged = true;
      return true;
    }
    return false;
  }

  handleMouseDragged(){
    const pointDragged = this.points.find(p => p.isBeingDragged);

    if (pointDragged) {
      pointDragged.set(mouseX, mouseY);
    }
  }

  handleMouseReleased(){
    this.points.forEach(p => { p.isBeingDragged = false; });
    this.isDragged = false;
  }

  draw(){
    P5JsUtils.applyStyleSet(this);
    triangle(this.p1.x, this.p1.y,
             this.p2.x, this.p2.y,
             this.p3.x, this.p3.y);

    if (this.dragEnabled) {
      P5JsUtils.drawControlPoints(this.points);
    }
  }
}

class Rectangle extends Rect {
  constructor(x, y, p_nWidth, p_nHeight){
    super(x, y, p_nWidth, p_nHeight);
    this.initPoints();
    this.computePoints();
    this.dragEnabled = false;
    this.dragArea = this;
    this.dragOffset = undefined;
    this.isDragged = false;
    this.fillColor = color(80);
  }

  setSize(newSize){
    let deltaWidth    = this.width - newSize;
    let deltaHeight   = this.height - newSize;

    // Maintain location
    this._x += deltaWidth / 2;
    this._y += deltaHeight / 2;

    this._width   = newSize;
    this._height  = newSize;
  }

  initPoints(){
    this.topLeft      = new Point(0, 0);
    this.topRight     = new Point(0, 0);
    this.bottomRight  = new Point(0, 0);
    this.bottomLeft   = new Point(0, 0);

    this.points = [];
    this.points.push(this.topLeft);
    this.points.push(this.topRight);
    this.points.push(this.bottomRight);
    this.points.push(this.bottomLeft);
  }

  computePoints(){
    this.topLeft.set(this.minX, this.minY);
    this.topRight.set(this.maxX, this.minY);
    this.bottomRight.set(this.maxX, this.maxY);
    this.bottomLeft.set(this.minX, this.maxY);
  }

  computePosAndSize(){
    const allX = this.points.map(p => p.x);
    const allY = this.points.map(p => p.y);

    const minX = Math.min(...allX);
    const minY = Math.min(...allY);
    const maxX = Math.max(...allX);
    const maxY = Math.max(...allY);

    this._x = minX;
    this._y = minY;
    this._width = maxX - minX;
    this._height = maxY - minY;
  }

  handleMousePressed(){
    const pointPressed = this.points.find(p => p.containsXY(mouseX, mouseY));

    if (pointPressed){
      pointPressed.isBeingDragged = true;
      this.isDragged = true;
      return true;
    } else {
      const dragAreaPressed = this.dragArea.containsXY(mouseX, mouseY);
      if (dragAreaPressed){
        this.isDragged = true;
        this.dragOffset = new Point(mouseX - this.x, mouseY - this.y);
        return true;
      }
    }
    return false;
  }

  handleMouseDragged(){
    const pointDragged = this.points.find(p => p.isBeingDragged);

    if (pointDragged) {
      pointDragged.set(mouseX, mouseY);

      if (this.topLeft == pointDragged) {
        this.bottomLeft.x = pointDragged.x;
        this.topRight.y = pointDragged.y;

      } else if (this.topRight == pointDragged) {
        this.bottomRight.x = pointDragged.x;
        this.topLeft.y = pointDragged.y;

      } else if (this.bottomRight == pointDragged) {
        this.topRight.x = pointDragged.x;
        this.bottomLeft.y = pointDragged.y;

      } else if (this.bottomLeft == pointDragged) {
        this.topLeft.x = pointDragged.x;
        this.bottomRight.y = pointDragged.y;
      }
      this.computePosAndSize();
    } else {
      this._x = mouseX - this.dragOffset.x;
      this._y = mouseY - this.dragOffset.y;
      this.computePoints();
    }
  }

  handleMouseReleased(){
    this.points.forEach(p => { p.isBeingDragged = false; });
    this.isDragged = false;
  }

  draw(){
    P5JsUtils.applyStyleSet(this);
    rect(this.x, this.y, this.width, this.height);

    if (this.dragEnabled) {
      P5JsUtils.drawControlPoints(this.points);
    }
  }
}

class Circle {
  constructor(x, y, radius){
    this.pos = createVector(x, y);
    this.radius = radius;
    this.debugMode = false;
    this.dragEnabled = false;
    this.isDragged = false;

    this.dragPos = new Point(0, 0);
    this.dragPos.pos = this.pos; 
    this.dragSize = new Point(this.x + this.radius, this.y);
    this.points = [this.dragPos, this.dragSize];
  }

  get x() { return this.pos.x; }
  get y() { return this.pos.y; }
  get minX() { return this.pos.x - this.radius; }
  get maxX() { return this.pos.x + this.radius; }
  get minY() { return this.pos.y - this.radius; }
  get maxY() { return this.pos.y + this.radius; }
  get centerX() { return this.pos.x; }
  get centerY() { return this.pos.y; }
  get width() { return this.radius * 2; }
  get height() { return this.radius * 2; }

  setSize(newSize){
    this.radius = newSize / 2;
  }

  pointsAtX(x) {
    if (x < this.minX || x > this.maxX) {
      return [];
    }

    // dx^2 + dy^2 = r^2
    // dy = (r^2 - dx^2) ^ 0.5
    const dx = this.x - x;
    const rSquared = Math.pow(this.radius, 2);
    const dy = Math.pow(rSquared - dx*dx, 0.5);

    return [{x: x, y: this.y + dy}, {x: x, y: this.y - dy}];
  }

  pointsAtY(y) {
    if (y < this.minY || y > this.maxY) {
      return [];
    }

    // dx^2 + dy^2 = r^2
    // dx = (r^2 - dy^2) ^ 0.5
    const dy = this.y - y;
    const rSquared = Math.pow(this.radius, 2);
    const dx = Math.pow(rSquared - dy*dy, 0.5);

    return [{x: this.x + dx, y: y}, {x: this.x - dx, y: y}];
  }

  handleMousePressed(){
    const pointPressed = this.points.find(p => p.containsXY(mouseX, mouseY));

    if (pointPressed){
      pointPressed.isBeingDragged = true;
      this.isDragged = true;
      return true;
    }
    return false;
  }

  handleMouseDragged(){
    const pointDragged = this.points.find(p => p.isBeingDragged);

    if (pointDragged) {
      if (this.dragSize == pointDragged) {
        pointDragged.x = mouseX;
        this.radius = dist(pointDragged.x, pointDragged.y, this.x, this.y);

      } else if (this.dragPos == pointDragged) {
        pointDragged.set(mouseX, mouseY);
        this.dragSize.y = pointDragged.y;
        this.dragSize.x = pointDragged.x + this.radius;
      }
    }
  }

  handleMouseReleased(){
    this.points.forEach(p => { p.isBeingDragged = false; });
    this.isDragged = false;

  }

  containsXY(x, y){
    return  dist(x, y, this.x, this.y) < this.radius;
  }

  tangentPoint(point, clockwiseWrap = true){
    const vec = createVector(this.x - point.x, this.y - point.y);
    const dist = vec.mag();
    if (dist < this.radius){
      return point;
    }

    const theta = Math.asin(this.radius / dist);
    const rotationAngle = clockwiseWrap ? -1 * theta : theta;
    vec.rotate(rotationAngle);
    vec.setMag(dist * Math.cos(theta) );
    return {x: point.x + vec.x, y: point.y + vec.y};
  }

  radialVector(point){
    return createVector(point.x - this.x, point.y - this.y);
  }

  static get TANGENT_MODE_POS_TO_POS() { return 1; }
  static get TANGENT_MODE_POS_TO_NEG() { return -1; }
  static get TANGENT_MODE_NEG_TO_POS() { return -2; }
  static get TANGENT_MODE_NEG_TO_NEG() { return 2; }

  tangentToCircle(other, mode = Circle.TANGENT_MODE_POS_TO_POS){
    const wrap = (mode == Circle.TANGENT_MODE_POS_TO_POS 
                    || mode == Circle.TANGENT_MODE_NEG_TO_POS);
    if (other.radius == undefined || other.radius == 0){
      const tangentPt = this.tangentPoint(other, wrap);
      return new LineSegment(other.x, other.y, 
                                  tangentPt.x, tangentPt.y);
    }

    const bigger  = (this.radius >= other.radius) ? this : other;
    const smaller = (this.radius >= other.radius) ? other : this;

    const modifier = Math.sign(mode);
    const virtualRadius = bigger.radius - (smaller.radius * modifier);
    const virtualCircle = new Circle(bigger.x, bigger.y, virtualRadius);

    const tangentPtOnVirtual = virtualCircle.tangentPoint(smaller, wrap);
    const radialLine = virtualCircle.radialVector(tangentPtOnVirtual);
    radialLine.setMag(smaller.radius);

    if (this.debugMode) {
      this.debugHelperObjects(virtualCircle, smaller, tangentPtOnVirtual, radialLine);  
    }

    const tangentSegment = new LineSegment(smaller.x, smaller.y, 
                                  tangentPtOnVirtual.x, tangentPtOnVirtual.y);
    tangentSegment.translate(modifier * radialLine.x, modifier * radialLine.y);
    return tangentSegment;
  }

  debugHelperObjects(virtualCircle, smaller, tangentPtOnVirtual, radialLine){
    stroke(50, 200, 40);
    virtualCircle.draw();

    stroke(200, 200, 40);
    line(smaller.x, smaller.y, tangentPtOnVirtual.x, tangentPtOnVirtual.y);

    let debugRadialSegment = new LineSegment(smaller.x, smaller.y,
                                             radialLine.x+smaller.x, radialLine.y+smaller.y);
    stroke(40, 200, 200);
    debugRadialSegment.draw();
  }

  draw(){
    P5JsUtils.applyStyleSet(this);
    ellipseMode(CENTER);
    ellipse(this.x, this.y, this.radius * 2, this.radius * 2);

    if (this.dragEnabled) {
      P5JsUtils.drawControlPoints([this.dragPos, this.dragSize]);
    }
  }
}

class Rect {
  constructor(x, y, p_nWidth, p_nHeight){
    this._x = x;
    this._y = y;
    this._width = p_nWidth;
    this._height = p_nHeight;
  }

  get x(){ return this._x; }
  get y(){ return this._y; }
  get width(){ return this._width; }
  get height(){ return this._height; }
  get minX(){ return this._x; }
  get minY(){ return this._y; }
  get maxX(){ return this._x + this._width; }
  get maxY(){ return this._y + this._height; }
  get centerX(){ return this._x + this._width / 2; }
  get centerY(){ return this._y + this._height / 2; }

  move(x, y){
    this._x += x;
    this._y += y;
  }

  copy(){
    return new Rect(this._x, this._y, this._width, this._height);
  }

  localRect(){
    return new Rect(0, 0, this.width, this.height);
  }

  contains(otherRect){
    return this.minX < otherRect.minX && this.maxX > otherRect.maxX
            && this.minY < otherRect.minY && this.maxY > otherRect.maxY;
  }

  expandToIncludeRect(otherRect){
    let maxX = this.maxX;
    let maxY = this.maxY;

    this._x = Math.min(this._x, otherRect._x);
    this._y = Math.min(this._y, otherRect._y);

    maxX = Math.max(maxX, otherRect.maxX);
    maxY = Math.max(maxY, otherRect.maxY);

    this._width  = maxX - this._x;
    this._height = maxY - this._y;
  }

  containsXY(x, y){ 
    return this.minX <= x && x < this.maxX 
           && this.minY <= y && y < this.maxY;
  }

  getConcentric(scale){
    let newX = this.x - (scale - 1) * this.width / 2;
    let newY = this.y - (scale - 1) * this.height / 2;
    return new Rect(newX, newY, this.width * scale, this.height * scale);
  }
}

class LineSegment {
  constructor(x1, y1, x2, y2){
    if (typeof x1 == "object" && typeof y1){
      this.start = x1;
      this.end = y1;
    } else {
      this.start = new Point(x1, y1);
      this.end = new Point(x2, y2);
    }
    this.points = [];
    this.points.push(this.start);
    this.points.push(this.end);

    this.dragEnabled = false;
    this.isDragged = true;
  }

  dx() { return this.endX - this.startX; }
  dy() { return this.endY - this.startY; }
  get slope() { return this.dy() / this.dx(); }

  get offset() {
    if (this.slope == Infinity) {
      return undefined;
    }
    return this.startY + this.slope * (0 - this.startX);
  }

  getLine(){
    let slope = this.slope;
    if (slope == Infinity || slope == -Infinity){
      return new VerticalLine(this.startX);
    } else if (slope == 0){
      return new HorizontalLine(this.startY);
    }

    let line = new Line(slope);
    line.offset = this.startY + slope * (0 - this.startX);
    return line;
  }

  translate(x, y){
    this.start.move(x, y);
    this.end.move(x, y);
  }

  set startX(val) { this.start.x = val; }
  set startY(val) { this.start.y = val; }
  set endX(val) { this.end.x = val; }
  set endY(val) { this.end.y = val; }
  get startX() { return this.start.x; }
  get startY() { return this.start.y; }
  get endX() { return this.end.x; }
  get endY() { return this.end.y; }


  handleMousePressed(){
    const pointPressed = this.points.find(p => p.containsXY(mouseX, mouseY));

    if (pointPressed){
      pointPressed.isBeingDragged = true;
      this.isDragged = true;
      return true;
    }
    return false;
  }

  handleMouseDragged(){
    const pointDragged = this.points.find(p => p.isBeingDragged);

    if (pointDragged) {
      pointDragged.set(mouseX, mouseY);
    }
  }

  handleMouseReleased(){
    this.points.forEach(p => { p.isBeingDragged = false; });
    this.isDragged = false;
  }

  draw(){
    P5JsUtils.applyStyleSet(this);
    line(this.startX, this.startY, this.endX, this.endY);

    if (this.dragEnabled) {
      P5JsUtils.drawControlPoints(this.points);
    }
  }
}

class Line {
  constructor(slope, offset){
    this.slope = slope;
    this.offset = offset;
  }

  valueAt(x){ return this.slope * x + this.offset; }

  reflectPoint(point){
    // following this simple explanation:
    // https://martin-thoma.com/reflecting-a-point-over-a-line/
    let bisector = this.perpendicularBisector();

    // with:  y = slope * x + offset, solve for Offset at known 'point'
    bisector.offset = point.y - bisector.slope * point.x;

    // solve for interset of this line, and its bisector through the 'point'
    let xIntersect = (bisector.offset - this.offset) / (this.slope - bisector.slope);
    let yIntersect = this.valueAt(xIntersect);

    let reflectedPoint = {x: undefined, y: undefined};
    reflectedPoint.x = point.x + 2 * (xIntersect - point.x);
    reflectedPoint.y = point.y + 2 * (yIntersect - point.y);
    return reflectedPoint;
  }

  perpendicularBisector(){
    return new Line(-1 / this.slope, undefined);
  }
}

// Special Cases below - to avoid divide-by-zero cases.

class VerticalLine {
  constructor(xOffset){
    this.slope = Infinity;
    this.offset = undefined;
    this.xOffset = xOffset;
  }

  valueAt(x){ return undefined; }

  reflectPoint(point){
    let reflectedPoint = {x: point.x, y: point.y};
    reflectedPoint.x = this.xOffset - (point.x - this.xOffset);
    return reflectedPoint;
  }

  perpendicularBisector(){
    return new HorizontalLine(0, undefined);
  }
}


class HorizontalLine {
  constructor(yOffset){
    this.slope = 0;
    this.offset = yOffset;
  }

  valueAt(x){ return this.offset; }

  reflectPoint(point){
    let reflectedPoint = {x: point.x, y: point.y};
    reflectedPoint.y = this.offset - (point.y - this.offset);
    return reflectedPoint;
  }

  perpendicularBisector(){
    return new VerticalLine(undefined);
  }
}

class Point {
  constructor(x, y){
    this.pos = createVector(x, y);
    this.radius = 10;
    this.isBeingDragged = false;
    this.dragEnabled = false;
  }

  get x(){ return this.pos.x; }
  get y(){ return this.pos.y; }
  set x(newVal){ this.pos.x = newVal; }
  set y(newVal){ this.pos.y = newVal; }

  set(x, y){
    this.x = x;
    this.y = y;
  }

  distTo(otherPoint){
    return dist(this.x, this.y, otherPoint.x, otherPoint.y);
  }

  move(x, y){
    this.x += x;
    this.y += y;
  }

  add(point){
    this.x += point.x;
    this.y += point.y;
  }

  // static get ALIGN_MODE_MOVE_1() { return 0; }
  // static get ALIGN_MODE_MOVE_2() { return 1; }
  static get ALIGN_MODE_MOVE_3() { return 2; }
  static get ALIGN_MODE_AVERAGE() { return 3; }

  static align(p1, p2, p3, mode){
    mode = mode ? mode : Point.ALIGN_MODE_MOVE_3;

    let vec12 = createVector(p2.x - p1.x, p2.y - p1.y);
    let vec21 = createVector(p1.x - p2.x, p1.y - p2.y);
    let vec23 = createVector(p3.x - p2.x, p3.y - p2.y);

    let heading12 = vec12.heading();
    let heading23 = vec23.heading();

    // let angBtw = vec21.angleBetween(vec23);
    // console.log(`angBtw: ${angBtw}, heading diff: ${heading12 - heading23}`);

    switch (mode) {
      case Point.ALIGN_MODE_MOVE_3:
        vec23.rotate(heading12 - heading23);
        p3.x = p2.x + vec23.x;
        p3.y = p2.y + vec23.y;
        return;
      case Point.ALIGN_MODE_AVERAGE:
        let angleDiff = heading12 - heading23; 
        vec23.rotate( );
        p3.x = p2.x + vec23.x;
        p3.y = p2.y + vec23.y;
        return;
    }
  }

  // NOTE: This function accepts alternate formatted params
  // (Point, heading)
  // (x, y, heading)
  rotateAbout(a, b, c){
    let otherPoint;
    let heading;

    if (a instanceof Point){
      otherPoint = a.pos;
      heading = b;
    } else {
      otherPoint = createVector(a, b);
      heading = c;
    }

    let diff = p5.Vector.sub(this.pos, otherPoint);
    diff.rotate(heading);
    this.x = otherPoint.x + diff.x;
    this.y = otherPoint.y + diff.y;
  }

  containsXY(x, y){
    return dist(x, y, this.x, this.y) < this.radius;
  }

  handleMousePressed(){
    this.isDragged = this.containsXY(mouseX, mouseY);
    return this.isDragged;
  }

  handleMouseDragged(){
    this.set(mouseX, mouseY);
  }

  handleMouseReleased(){
    this.isDragged = false;
  }

  draw(){
    if (this.dragEnabled) {
      P5JsUtils.drawControlPoints([this]);
    }
    point(this.x, this.y);
  }
}

