let scalar = 0.8; //a made up number for each font
var asc //textAscention size
var spaceWidth //how big is a space?
var img
var fontSize = 30
var leading



function calculateFontSize(theText, w, h, font, tSize){
  var p = 0 //iterator to see how many times it took to "fill the space"

  //let scalar = 0.7; //a made up number for each font
  asc = textAscent() * scalar; // Calc ascent 
  var spaceWidth //the width of space char
  //var gutterSize = 4
  var words = theText.split(" ");
  print(words.length, "words")

var y = 0
  while( y < h){
    var x = 0
    var currentOffset = 0; //rightwards value
    y = 0
     var lineNum = 0;

      for (var j = 0; j < words.length; j++) {
        textSize(tSize);
        leading = textLeading() * 0.75

        spaceWidth = textWidth(" ")
        var wordWidth = textWidth(words[j]);
        asc = textAscent() * scalar; // Calc ascent 

        x = spaceWidth + currentOffset  //xpos
        var rightEdge = x + wordWidth

        if (rightEdge > w - spaceWidth){
          //print("rightEdge", rightEdge, x, j)
          currentOffset = 0
          x = wordWidth + currentOffset //xpos
          lineNum ++

        }
        y = lineNum * tSize + leading  //+ asc
         if (currentOffset == 0){
          spaceWidth = textWidth(" ")
        }else{
          spaceWidth = textWidth(" ")
        }       

        fill(0,200, 200, x)
        //print(x, y, wordWidth, tSize)
        rect(x, y , wordWidth, tSize)
        fill("red")
        text(words[j], x, y , wordWidth, tSize);///don't draw

        // four pixels between words
        currentOffset += wordWidth + spaceWidth; 

    }

    if (y - tSize  > h -tSize ){
       print ("this took",p, "times")
      //print( x, y, w, h - tSize, tSize )

       textSize(tSize);
       return tSize 
    }else{

      textSize(tSize);

    }
   tSize += 0.5 //change this to reduce/increase the number of iterations
    //and speed  to calculate
   p++  
  }

  return tSize 

}

let h = Math.sqrt(3) / 2 * 100;
let shapes;
let hex;

function colorFromPalette(n) {
  const palette = ['#fc71ee', '#3f0f6e', '#030a24', '#222152', '#00f7eb'];
  return palette[n % (palette.length)];
}

function draw() {
  background(colorFromPalette(2));

  drawTriangle();
  drawHex();

  push();
  imageMode(CENTER);

  translate(200, 200);
  scale(0.5);

  translate(h * -2, -300);
  tessellate(3);
  translate(-h, 150);
  tessellate(4);
  translate(-h, 150);
  tessellate(5);
  translate(h, 150);
  tessellate(4);
  translate(h, 150);
  tessellate(3);

  pop();
}

function setup() {
  createCanvas(400, 400);

  h = Math.sqrt(3) / 2 * 100;

  shapes = createGraphics(100, Math.ceil(h));
  shapes.noStroke();
  shapes.angleMode(DEGREES);

  hex = createGraphics(200, 200);
  hex.angleMode(DEGREES);
}

function drawTriangle() {
  const ctx = shapes.drawingContext;
  ctx.save();
  ctx.beginPath();
  ctx.moveTo(50, 0);
  ctx.lineTo(100, h);
  ctx.lineTo(0, h);
  ctx.clip();
  shapes.clear();
  shapes.rotate(frameCount);
  shapes.fill(colorFromPalette(0));
  shapes.rect(20, 20, 50, 50);
  shapes.fill(colorFromPalette(2));
  shapes.triangle(0, 10, 80, 90, 0, 100);
  shapes.fill(colorFromPalette(1));
  shapes.triangle(20, 0, 50, 30, 30, 60);
  shapes.fill(colorFromPalette(4));
  shapes.ellipse(100, 50, 80);
  shapes.fill(colorFromPalette(1));
  shapes.ellipse(-50, -50, 50);
  shapes.fill(colorFromPalette(5));
  shapes.ellipse(-40, -46, 20);
  shapes.fill(colorFromPalette(0));
  shapes.triangle(-60, 0, -30, -40, -30, 0);
  shapes.fill(colorFromPalette(3));
  shapes.rect(-45, 0, 40, 300);
  shapes.rotate(17);
  shapes.fill(colorFromPalette(4));
  shapes.rect(30, 40, 10, 40);
  shapes.rotate(37);
  shapes.fill(colorFromPalette(6));
  shapes.rect(30, 40, 20, 40);
  shapes.rotate(180);
  shapes.fill(colorFromPalette(0));
  shapes.triangle(10, 20, 80, 90, 0, 100);
  shapes.translate(20, 0);
  shapes.rotate(20);
  shapes.fill(colorFromPalette(3));
  shapes.rect(0, 0, 20, 200);
  ctx.restore();
}

function drawHex() {
  hex.push();
  hex.clear();
  hex.translate(100, 100);
  hex.push();
  hex.scale(1 / pixelDensity());
  hex.rotate(30);
  for (let i = 0; i < 3; i++) {
    hex.drawingContext.drawImage(shapes.elt, -50 * pixelDensity(), 0);
    hex.scale(-1, 1);
    hex.rotate(60);
    hex.drawingContext.drawImage(shapes.elt, -50 * pixelDensity(), 0);
    hex.rotate(60);
    hex.scale(-1, 1);
  }
  hex.pop();
  hex.pop();
}

function tessellate(n) {
  push();
  for (let i = 0; i < n; i++) {
    push();
    scale(1 / pixelDensity());
    drawingContext.drawImage(hex.elt, -100 * pixelDensity(), -100 * pixelDensity());
    pop();
    translate(h * 2, 0);
  }
  pop();
}

