const modColor = () => {
  let vAlpha = 1
  let vr = 1
  let vg = 1
  let vb = 1
  return ({
    r,
    g,
    b,
    alpha
  }) => {
    r = (r + (0.1 * vr))
    g = (g + (0.5 * vg))
    b = (b + (1 * vb))
    alpha = (alpha + (0.1 * vAlpha))
    vAlpha = (alpha > 200 || alpha <= 20) ? -vAlpha : vAlpha
    vr = (r > 255 || r < 1) ? -vr : vr
    vg = (g > 255 || g < 1) ? -vg : vg
    vb = (b > 255 || b < 1) ? -vb : vb
    return {
      r,
      g,
      b,
      alpha
    }
  }
}

