      rods[i].display();
    }
  }

  function rod(_xStart, _yStart, _length, _speed, _size) {
    this.ang = 0;
    this.update = function() {
      this.ang += _speed;
      this.xEnd = _length * sin(this.ang);
      this.yEnd = _length * cos(this.ang);
    }

    this.display = function() {
      stroke(255);
      strokeWeight(2);
      ellipse(this.xEnd, this.yEnd, _size);
    }
  }

