    "IMG_" +
    year() +
    "-" +
    month() +
    "-" +
    day() +
    "_" +
    hour() +
    "-" +
    minute() +
    "-" +
    second();
  saver(context.drawingContext.canvas, croppedVectors, name);
  console.log("downloaded " + name);
}

