    sprite1.position.x -= 5;
  }
  if (keyIsDown(RIGHT_ARROW)) {
    sprite1.position.x += 5;
  }
  if (keyIsDown(UP_ARROW)) {
    sprite1.position.y -= 5;
  }
  if (keyIsDown(DOWN_ARROW)) {
    sprite1.position.y += 5;
  }

  // Calculate distance between sprites
  let d = dist(sprite1.position.x, sprite1.position.y, sprite2.position.x, sprite2.position.y);

  // Move sprite2 away if too close
  if (d < safeDistance) {
    let angle = atan2(sprite2.position.y - sprite1.position.y, sprite2.position.x - sprite1.position.x);
    sprite2.position.x += cos(angle) * 5;
    sprite2.position.y += sin(angle) * 5;
  }

  // drawSprites();
}

