class TextInfo {
  constructor(p, v, a) {
    this.pos = createVector(p.x, height * p.y);
    this.vel = createVector(v, 0);
    this.acc = createVector(a, 0);
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);

    return this;
  }

  display(img) {
    image(img, 0, 0);

    return this;
  }

  stop() {
    if (this.pos.x >= width * 0.7) {
      this.vel.x = 0;
      this.acc.mult(0);
    }

    return this;
  }

  render(img) {
    return this.update().display(img).stop();
  }
}

