// Main
var bgColor;
var dragDropCanvas;
var droppedFile;
var imgBuffer;
var imgOriginal;
var imgCopy;

// Specific
var drawing = 0;

var stretchSW = 2.0;
var transition = false;
var transitionRes = 5;
var firefox = false;
var firefoxRes = 4;
var drawTranslation;

// Interface
var grid = 10;
var interfaceColor;
var interfaceSW = 3.0;
var fromP1, fromP2, toP1, toP2;

class Shape {
  constructor() {
    this.vectors = [];
  }

  addVector(x, y) {
    this.vectors.push(new p5.Vector(x, y));
  }

  draw() {
    strokeJoin(ROUND);
    strokeWeight(interfaceSW);
    stroke(interfaceColor);
    noFill();
    beginShape();
    for (let v of this.vectors) {
      vertex(v.x, v.y);
    }
    endShape(CLOSE);

    beginShape();
    strokeWeight(interfaceSW * 5);

    for (let v of this.vectors) {
      vertex(v.x, v.y);
      point(v.x, v.y);
      point(gridify(mouseX), gridify(mouseY));
    }
    endShape(CLOSE);
  }

  fill() {
    fill("blue");
    beginShape();
    for (let v of this.vectors) {
      vertex(v.x, v.y);
    }
    endShape(CLOSE);
  }
}

let shape1 = new Shape();

