function uploadFile() {
  var selectedFile = document.getElementById("file-input");
  var myImageFile = selectedFile.files[0];
  var urlOfImageFile = URL.createObjectURL(myImageFile);
  imgOriginal = loadImage(urlOfImageFile, translateFile);
